/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){

	var result = true;

	var PaValue = cmbWnDatS.Value;
	var UrValue = cmbWnDatE.Value;
	if (PaValue == "" || UrValue == "")
	{
		alert(setPopMessage("3048"));
		result = false;
	}
	else if(PaValue > UrValue)
	{
		alert(setPopMessage("3049"));
		result =false;
	}
	return result;
}

