 * 내용설명 : 설비번호 확인
 **********************************************************************/ 
 function txtmshno_chk(){

 		if(trim(txtmshno.Text) != ""){
		/*
		var chk = chk_mshno(trim(txtmshno.Text));
		if(chk != ""){
			alert(chk);
			return;
		}
		*/

		//var ret = GetPrtcdTimcdByMshno(comps,trim(txtmshno.text));
		GetPrtcdTimcdByMshno(ComboBoxPlant.Value,trim(txtmshno.text));
		if(ds_ogm200_chk.GetColCount()==0){
			alert(setPopMessage("2526"));
			gmshnm="";
			gprtcd="";	
			gtimcd="";
			txtmshno.Text="";
			txtmshnm.Text="";
		}else{
			gmshnm = ds_ogm200_chk.GetColumn(0,"mshnm");
			gprtcd = ds_ogm200_chk.GetColumn(0,"prtcd");
			gtimcd = ds_ogm200_chk.GetColumn(0,"timcd");
			timnm = ds_ogm200_chk.GetColumn(0,"timnm");
			prtnm = ds_ogm200_chk.GetColumn(0,"prtnm");
			indat = ds_ogm200_chk.GetColumn(0,"indat");
//alert("선택gtimcd==="+gtimcd);
//alert("선택gprtcd==="+gprtcd);			
			txtmshnm.Text = gmshnm; 
			txttimnm.Text = timnm; 
			txtprtnm.Text = prtnm; 
			mtbindat.Text = substr(indat,0,6); 
			//cmbjobcd.Focus();
		}
	}
	else{
		alert(setPopMessage("3019"));
	}
} 
/********************************************************************** 
 * 내용설명 : 설비체크
 **********************************************************************/ 

function GetPrtcdTimcdByMshno(selcomp,mshno)
{
	var sKind		= "getOgm200_chk";						
	var sMethodName = "service::psogmlib/selectOgm200_chk.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ogm200_chk=ds_ogm200_chk";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(selcomp); //사업부
		sArgument += " MSHNO=" + quote(mshno); //설비No
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");	
}

/********************************************************************** 
 * 내용설명 : 설비No 입력시 asc code 체크
 **********************************************************************/ 
function chk_mshno(mshno)
{
	var msg = "";
	for(var i = 0 ; i < mshno.length; i++){
		var ch =Asc(mshno.CharAt(i));

		switch(i)
		{
			case 0:
			case 1:
				if((ch < 48) || (ch > 57 && ch < 65) || (ch > 90 && ch < 97) || (ch > 122))
				{	
					gmshnm="";
					gprtcd="";	
					gtimcd="";
					txtmshno.Text="";
					txtmshnm.Text="";
					msg = "1,2번째자리에는 숫자 또는 영문자가 와야 합니다";
					return msg;
				}
				break;
			case 2:
				if((ch != 32 && ch < 65) || (ch > 90 && ch < 97) || (ch > 122))   
				{
					gmshnm="";
					gprtcd="";	
					gtimcd="";
					txtmshno.Text="";
					txtmshnm.Text="";
					msg = "3번째자리에는 영문자나 공란이 와야 합니다.";
					return msg;
				}
				break;
			case 3:
				if(ch != 32)
				{
					gmshnm="";
					gprtcd="";	
					gtimcd="";
					txtmshno.Text="";
					txtmshnm.Text="";
					msg = "4번째자리는 공란이 와야 합니다.";
					return msg;
				}
				break;
			case 4:
			case 5:
			case 6:
			case 7:
				if(ch < 48 || ch > 57)
				{
					gmshnm="";
					gprtcd="";	
					gtimcd="";
					txtmshno.Text="";
					txtmshnm.Text="";
					msg = "5 ~ 8번째짜리까지는 숫자가 와야 합니다.";
					return msg;
				}
				break;
			case 8:
			case 9:
				if((ch != 32 && ch < 48) || (ch  > 57 ))
				{
					gmshnm="";
					gprtcd="";	
					gtimcd="";
					txtmshno.Text="";
					txtmshnm.Text="";
					msg = "8 ~ 10번째자리까지는 공란이나 숫자가 와야 합니다";
					return msg;
				}
				break;
			}
		}
	return msg;
}

