function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value=comps;
//	ComboBoxPlant.Enable = false; 
}

function getDesign(){	
	//Grid_ogm316.ToolTipText = setPopMessage("2053");
	Grid_ogm316.BkColor = G_ds_design.GetColumn(0,"BkColor");
	Grid_ogm316.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	Grid_ogm316.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	Grid_ogm316.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	Grid_ogm316.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	Grid_ogm316.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");		
}

/********************************************************************** 
 * 내용설명 : 조회
 **********************************************************************/ 
 function getOgm316(lincd)
{
	var sKind		= "getOgm316";						
	var sMethodName = "service::psogmlib/selectOgm316.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ogm316=ds_ogm316";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " LIB3=" + quote(Lib3);
		sArgument += " COMPS=" + quote(comps1); //사업부
		sArgument += " WNDATS=" + quote(wndates); //
		sArgument += " WNDATE=" + quote(wndatee); //
		sArgument += " LINCD=" + quote(lincd); //
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
/*
	http.Sync = true;
	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	http.Sync = false;
	if(ds_ogm316.rowcount <= 0){
		alert(setPopMessage("1003"));	
	}
*/	
	cal_ogm316();
}
/********************************************************************** 
 * 내용설명 : 합계구하기
 **********************************************************************/ 
function cal_ogm316(){

	var stphr = 0.0;
	var tothr = 0.0;
	var nocnt = 0;
	var pcost = 0.0;
	var scost = 0.0;
	var ecost = 0.0;

	for (var i=0;i<ds_ogm316.GetRowCount();i++)
	{
		stphr = stphr+ parseFloat(ds_ogm316.getColumn(i,"STPHR"));
		tothr = tothr+ parseFloat(ds_ogm316.getColumn(i,"TOTHR"));
		pcost = pcost+ parseFloat(ds_ogm316.getColumn(i,"PCOST"));
		scost = scost+ parseFloat(ds_ogm316.getColumn(i,"SCOST"));
		ecost = ecost+ parseFloat(ds_ogm316.getColumn(i,"ECOST"));
	}
	ctxtStpHr.Text=NumFormat(stphr,1);
	ctxtTotHr.Text=NumFormat(tothr,1);
	ctxtNoCnt.Text=ds_ogm316.GetRowCount();
	ctxtPCost.Text=NumFormat(pcost,1);
	ctxtSCost.Text=NumFormat(scost,1);
	ctxtECost.Text=NumFormat(ecost,1);

}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="getOgm316"){
	// 조회건수가 없을경우
		if(ds_ogm316.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}
		// tran 성공	
	}else if(svcId=="setUserList"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}
}


/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "부문별 보전실적상세(사후,예방,개량)";
	var arrSheets  =  Array();  

	arrSheets[0]  = "Grid_ogm316|라인별 보전실적상세(사후,예방,개량)";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

/********************************************************************** 
 * 내용설명 : 데이터셋 상태 체크
 **********************************************************************/ 
function ds_ogm316_OnRowPosChanged(obj,nOldRow,nRow){
	//로우타입 insert만 ED박스 enable
	if(ds_ogm316.GetRowType(ds_ogm316.currow)=="insert"){
		ed_maker.Enable=true;
	}else{
		ed_maker.Enable=false;
	}
}


