function onload_ogm316(){

	txtwndats1.Text = "";
	txtwndate1.Text = "";
	txtLinCd.Value = "";
	txtLinNm.Value = "";
	ctxtStpHr.value = "";
	ctxtTotHr.Value = "";
	ctxtNoCnt.Value = "";
	ctxtPCost.Value = "";
	ctxtSCost.Value = "";
	ctxtECost.Value = "";

	//var lincd=trim(ds_ogm311.lincd);
	//var linnm=trim(ds_ogm311.linnm);
	var lincd=trim(ds_ogm311.GetColumn(ds_ogm311.currow,"LINCD"));
	var linnm=trim(ds_ogm311.GetColumn(ds_ogm311.currow,"LINNM"));

	txtLinCd.Text=lincd;
	txtLinNm.Text=linnm;
	txtwndats1.Text=wndates;
	txtwndate1.Text=wndatee;
	getOgm316(lincd);
}


