/********************************************************************** 
 * 내용설명 : on_LoadCompleted 조회
 **********************************************************************/ 
function getOgm317_onload(wndats, wndate, prtcd, lincd, mshno, jobcd, sdpcd, wrkm1, CheckedCount, rvalue){

//alert(CheckedCount);
	var sKind		= "getOgm317_onload";						
	var sMethodName = "service::psogmlib/selectOgm317_onload.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_obj200pf=ds_obj200pf";
	var sArgument   = "";
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(comps1);
		sArgument += "@WNDATS=" + quote(wndats);
		sArgument += "@WNDATE=" + quote(wndate);
		sArgument += "@PRTCD=" + quote(prtcd);
		sArgument += "@LINCD=" + quote(lincd);
		sArgument += "@MSHNO=" + quote(mshno);
		sArgument += "@JOBCD=" + quote(jobcd);
		sArgument += "@SDPCD=" + quote(sdpcd);
		sArgument += "@WRKM1=" + quote(wrkm1);
		sArgument += "@SELCD=" + quote(selcd);
		sArgument += "@KOGUBCHK=" + quote(parseInt(CheckedCount));
		sArgument += "@KOGUB=" + quote(rvalue);
	http.Sync = true;
	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	http.Sync = false;

	if (str == 'T'){  //정상조회
		var sum_stphr = 0.0;
		var sum_tothr = 0.0;
		var cnt_tothr = 0;
		var sum_bm = 0.0;
		var cnt_bm = 0;
		var sum_pm = 0.0;
		var cnt_pm = 0;
		var sum_cm = 0.0;
		var cnt_cm = 0;
		var sum_pcost = 0.0;
		var sum_scost = 0.0;
		var sum_ecost = 0.0;
		var cnt_repcd = 0;	
		for (var i=0;i<ds_obj200pf.GetRowCount();i++)
		{
			sum_stphr = sum_stphr+ parseFloat(ds_obj200pf.getColumn(i,"SUM_STPHR"));
			sum_tothr = sum_tothr+ parseFloat(ds_obj200pf.getColumn(i,"SUM_TOTHR"));
			cnt_tothr = cnt_tothr+ parseInt(ds_obj200pf.getColumn(i,"CNT_TOTHR"));
			sum_bm = sum_bm+ parseFloat(ds_obj200pf.getColumn(i,"SUM_BM"));
			cnt_bm = cnt_bm+ parseInt(ds_obj200pf.getColumn(i,"CNT_BM"));
			sum_pm = sum_pm+ parseFloat(ds_obj200pf.getColumn(i,"SUM_PM"));
			cnt_pm = cnt_pm+ parseInt(ds_obj200pf.getColumn(i,"CNT_PM"));
			sum_cm = sum_cm+ parseFloat(ds_obj200pf.getColumn(i,"SUM_CM"));
			cnt_cm = cnt_cm+ parseInt(ds_obj200pf.getColumn(i,"CNT_CM"));
			sum_pcost = sum_pcost+ parseFloat(ds_obj200pf.getColumn(i,"SUM_PCOST"));
			sum_scost = sum_scost+ parseFloat(ds_obj200pf.getColumn(i,"SUM_SCOST"));
			sum_ecost = sum_ecost+ parseFloat(ds_obj200pf.getColumn(i,"SUM_ECOST"));
			cnt_repcd = cnt_repcd+ parseInt(ds_obj200pf.getColumn(i,"CNT_REPCD"));
		}
		txtStpHr.Text=NumFormat(sum_stphr,1);
		txtTotHr.Text=NumFormat(sum_tothr,1);
		txtNoCnt.Text=cnt_tothr;
		txtBmHr.Text=NumFormat(sum_bm,1);
		txtBmCnt.Text=cnt_bm;
		txtPmHr.Text=NumFormat(sum_pm,1);
		txtPmCnt.Text=cnt_pm;
		txtCmHr.Text=NumFormat(sum_cm,1);
		txtCmCnt.Text=cnt_cm;
		txtPCost.Text=NumFormat(sum_pcost,1);
		txtSCost.Text=NumFormat(sum_scost,1);
		txtECost.Text=NumFormat(sum_ecost,1);
		txtRepCnt.Text=cnt_repcd;

		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="getOgm317_onload"){
	// 조회건수가 없을경우
		if(ds_obj200pf.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}
		// tran 성공	
	}else if(svcId=="SetOgm317_onload"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}
}


