function onLoading(){

	var wndats = wndats1;
	var wndate = wndate2;
	var prtcd = cmbprtcd.Value;
	var lincd = cmblincd.Value;
	var mshno = trim(txtmshno.Text);

	var jobcd = cmbJobCd.Value;
	var sdpcd = cmbsdpcd.Value;
	var wrkm1 = trim(txtwrkm1.Text);
	var kdown = cmbkdown.Value;

	var CheckedCount = parseInt(ckbBM.Value)+parseInt(ckbPM.Value)+parseInt(ckbCM.Value);//합이 3
	var rvalue = " (";
	var checking = 0;
	if (ckbBM.Value == 1){ //ckbBM.Value가 체크되어있으면 1...
		checking = checking+1;
		rvalue = rvalue+"'"+"BM"+"'";
	}
	if (ckbPM.Value == 1){
		if (checking != 0) rvalue = rvalue+",";
		checking = checking+1;
		rvalue = rvalue+"'"+"PM"+"'";
	}
	if (ckbCM.Value == 1){
		if (checking != 0) rvalue = rvalue+",";
		checking = checking+1;
		rvalue = rvalue+"'"+"CM"+"'";
	}
	rvalue = rvalue+")";

	switch(selcd)
	{
		case "subas" :
			txtselcd.Text = "SUB ASSY분류";
			break;
		case "comcd" :
			txtselcd.Text = "셋트부품";
			break;
		case "deccd" :
			txtselcd.Text = "분해불가부품";
			break;
		case "chocd" :
			txtselcd.Text = "처치";
			break;
		case "hyocd" :
			txtselcd.Text = "현상";
			break;
		case "caucd" :
			txtselcd.Text = "원인";
			break;
		case "trucd" :
			txtselcd.Text = "실원인";
			break;
	}
	dtpwndats1.Value=wndats;
	dtpwndate1.Value=wndate;
	getOgm317_onload(wndats, wndate, prtcd, lincd, mshno, jobcd, sdpcd, wrkm1, CheckedCount, rvalue);

}

