function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	ComboBoxPlant.Value=comps;
	ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//tblInqResult.ToolTipText = setPopMessage("2053");
	tblInqResult.BkColor = G_ds_design.GetColumn(0,"BkColor");
	tblInqResult.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	tblInqResult.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	tblInqResult.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	tblInqResult.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	tblInqResult.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	tblInqResult.Width = G_ds_design.GetColumn(0,"Width");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}


function getOgm318_onload(wndats, wndate, prtcd, lincd, mshno, jobcd, sdpcd, wrkm1, selcd, selvalue1, CheckedCount, rvalue){
//alert(selvalue1+"^");
	var sKind		= "getOgm318_onload";						
	var sMethodName = "service::psogmlib/selectOgm318_onload.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_obj200pf_16=ds_obj200pf_16";
	var sArgument   = "";
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@LIB3=" + quote(Lib3);
		sArgument += "@COMPS=" + quote(comps1);
		sArgument += "@WNDATS=" + quote(wndats.Value);
		sArgument += "@WNDATE=" + quote(wndate.Value);
		sArgument += "@PRTCD=" + quote(prtcd);
		sArgument += "@LINCD=" + quote(lincd);
		sArgument += "@MSHNO=" + quote(mshno);
		sArgument += "@JOBCD=" + quote(jobcd);
		sArgument += "@SDPCD=" + quote(sdpcd);
		sArgument += "@WRKM1=" + quote(wrkm1);
		sArgument += "@SELCD=" + quote(selcd);
		sArgument += "@SELVALUE=" + quote(selvalue1);
		sArgument += "@SELCD2=" + quote(trim(lblselcd2_318.Text));
		sArgument += "@SELVALUE2=" + quote(trim(lblselvalue2_318.Text));
		sArgument += "@KOGUBCHK=" + quote(parseInt(CheckedCount));
		sArgument += "@KOGUB=" + quote(rvalue);
/*
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
*/
	http.Sync = true;
	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	http.Sync = false;
	if (str == 'T'){  //정상조회
		//alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="getOgm318_onload"){
	// 조회건수가 없을경우
		if(ds_obj200pf_16.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}
		// tran 성공	
	}else if(svcId=="setOgm318"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}
}

