	Grid_ogm326.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	Grid_ogm326.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	Grid_ogm326.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	Grid_ogm326.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

/********************************************************************** 
 * 내용설명 : 조회
 **********************************************************************/ 
 function getOgm326()
{
	var sdpcd="";
	var sdpcdVal=trim(txtSdpCd.Text);
	if(length(sdpcdVal)!=0){
		sdpcd=replace(sdpcdVal,"*","%");
	}	

	var sKind		= "getOgm326";						
	var sMethodName = "service::psogmlib/selectOgm326.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ogm326=ds_ogm326";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " LIB3=" + quote(Lib3);
		sArgument += " COMPS=" + quote(comps1); //사업부
		sArgument += " WNDATS=" + quote(wndates); //
		sArgument += " WNDATE=" + quote(wndatee); //
		sArgument += " SDPCD=" + quote(sdpcd); //
	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_ogm326.rowcount() <=0){
		alert(setPopMessage("1003"));
	}

	cal_ogm326();
}
/********************************************************************** 
 * 내용설명 : 합계구하기
 **********************************************************************/ 
function cal_ogm326(){

	var stphr = 0.0;
	var tothr = 0.0;
	var nocnt = 0;
	var pcost = 0.0;
	var scost = 0.0;
	var ecost = 0.0;

	for (var i=0;i<ds_ogm326.GetRowCount();i++)
	{
		stphr = stphr+ parseFloat(ds_ogm326.getColumn(i,"STPHR"));
		tothr = tothr+ parseFloat(ds_ogm326.getColumn(i,"TOTHR"));
		pcost = pcost+ parseFloat(ds_ogm326.getColumn(i,"PCOST"));
		scost = scost+ parseFloat(ds_ogm326.getColumn(i,"SCOST"));
		ecost = ecost+ parseFloat(ds_ogm326.getColumn(i,"ECOST"));
	}
	ctxtStpHr.Text=NumFormat(stphr,1);
	ctxtTotHr.Text=NumFormat(tothr,1);
	ctxtNoCnt.Text=ds_ogm326.GetRowCount();
	ctxtPCost.Text=NumFormat(pcost,1);
	ctxtSCost.Text=NumFormat(scost,1);
	ctxtECost.Text=NumFormat(ecost,1);

}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="getOgm326"){
	// 조회건수가 없을경우
		if(ds_ogm326.rowcount <= 0){
			alert(setPopMessage("1003"));
			setMessage("1003");	
		// 정상 조회
		} else {
			alert(setPopMessage("1004"));
			setMessage("1004");				
		}
		// tran 성공	
	}else if(svcId=="setUserList"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}
}


/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "실시부서별 보전실적 상세";
	var arrSheets  =  Array();  

	arrSheets[0]  = "Grid_ogm326|실시부서별 보전실적 상세";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

/********************************************************************** 
 * 내용설명 : 데이터셋 상태 체크
 **********************************************************************/ 
function ds_ogm326_OnRowPosChanged(obj,nOldRow,nRow){
	//로우타입 insert만 ED박스 enable
	if(ds_ogm326.GetRowType(ds_ogm326.currow)=="insert"){
		ed_maker.Enable=true;
	}else{
		ed_maker.Enable=false;
	}
}


