function onload_ogm326(){

	txtwndats1.Text = "";
	txtwndate1.Text = "";
	txtSdpCd.Value = "";
	txtSdpNm.Value = "";
	ctxtStpHr.value = "";
	ctxtTotHr.Value = "";
	ctxtNoCnt.Value = "";
	ctxtPCost.Value = "";
	ctxtSCost.Value = "";
	ctxtECost.Value = "";

	//var likcd=ds_ogm320_likcd.likcd;
	//var liknm=ds_ogm320_likcd.liknm;
	var likcd=ds_ogm320_likcd.likcd;
	var liknm=ds_ogm320_likcd.liknm;

	txtSdpCd.Text=trim(ds_ogm320_likcd.GetColumn(ds_ogm320_likcd.currow,"LIKCD"));
	txtSdpNm.Text=trim(ds_ogm320_likcd.GetColumn(ds_ogm320_likcd.currow,"LIKNM"));
	txtwndats1.Text=wndates;
	txtwndate1.Text=wndatee;
	getOgm326();
}


