function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	getComCode(); //공장구분 선택
	rdoggub.Value="'1','2','3'";
	sstand.Value = "04";
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)
	txtyedat_OnKillFocus(obj);	
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");	
	http.Sync=false;
	ComboBoxPlant.Value=comps;
//	ComboBoxPlant.Enable = false; 
}

function getDesign(){	
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");

	//searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

/********************************************************************** 
 * 내용설명 : on_LoadCompleted 조회
 **********************************************************************/ 
function getOgm401(){

	if(txtyedat.Value==""){
		alert("기준년월을 선택하세요.");
		return;
	}
	sstand_OnClick();

//alert(stdate+" "+enddate);	
//alert(strval);	
	var sKind		= "getOgm401";						
	var sMethodName = "service::psogmlib/selectOgm401.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ogm401_pre=ds_ogm401";
	var sArgument   = "";
		sArgument += "@LIB2=" + quote(Lib2);		//Lib
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value);			//사업부
		//sArgument += "@YEAR=" + quote(txtyear.Text);	//년도
		sArgument += "@WNDATS=" + quote(stdate);	//년도
		sArgument += "@WNDATE=" + quote(enddate);	//년도
		//sArgument += "@WNDATE2=" + quote(enddate2);	//
		sArgument += "@G_GUB=" + quote(rdoggub.Value);	//교정구분
		sArgument += "@STRVAL=" + quote(strval);	//조회데이터SQL
//alert(strval);	
	http.Sync = true;
	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	http.Sync = false;
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
	//값이 없는 부서삭제

	var rowcnt = ds_ogm401_pre.GetRowCount();
	for(var i=rowcnt; i >=0; i--){
		if(length(ds_ogm401_pre.GetColumn(i, "CNT_TOTAL"))==0){
			ds_ogm401_pre.DeleteRow(i);
		}
	}

	ds_ogm401.Copy(ds_ogm401_pre);
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="setogm200"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}	
}

/********************************************************************** 
 * 내용설명 : 데이터셋 상태 체크
 **********************************************************************/ 
function ds_ogm401_OnRowPosChanged(obj,nOldRow,nRow){
	//로우타입 insert만 ED박스 enable
	if(ds_ogm401.GetRowType(ds_ogm401.currow)=="insert"){
		ed_maker.Enable=true;
	}else{
		ed_maker.Enable=false;
	}
}

