function setExcel()
{
	var stitle = txtyear.Text + " "+ttitle.Value;
	ds_title.InsertRow(0);
	ds_title.SetColumn(0,"title", stitle);

	var fName   = "월별계획대실적";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|월별계획대실적";
	createExcelSheetS3(FileDialog0, fName ,arrSheets ,ds_title);

}

