/********************************************************************** 
 * 내용설명 : 조회
 **********************************************************************/ 
function getOgm410()
{
	if(!validationChk())
		return;
	lblNewOrInq.Text = "Inq";

	var sKind		= "getOgm410";						
	var sMethodName = "service::psogmlib/selectOgm410.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ogm410=ds_ogm410";
	var sArgument   = "";
		sArgument += "@LIB2=" + quote(lib2);
		//sArgument += "@COMPS=" + quote("H1"); //사업부
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@GROUP=" + quote(grboption.Value); //교정분야
		sArgument += "@MNGNO=" + quote(mngno); //관리번호

	http.Sync = true;
	var str = dialog("Common::PGMLOAD.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	http.Sync = false;
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
	//rdogroup1.TriggerEvent(EventNameConst.OnClick);
	cmbdptcd.Value = trim(ds_ogm410.GetColumn(0,"DPTCD"));
	txtmname.Text = trim(ds_ogm410.GetColumn(0,"MNAME"));

	txtmaker.Text = trim(ds_ogm410.GetColumn(0,"MAKER"));
	txtmtype.Text = trim(ds_ogm410.GetColumn(0,"MTYPE"));
	txtjnbno.Text = trim(ds_ogm410.GetColumn(0,"JNBNO"));
	cmbjkind.Value = trim(ds_ogm410.GetColumn(0,"JKIND"));
	txtipdat.Value = trim(ds_ogm410.GetColumn(0,"IPDAT"));
	txtcnote.Text = trim(ds_ogm410.GetColumn(0,"CNOTE"));

	cmbjgub.Value = trim(ds_ogm410.GetColumn(0,"J_GUB"));
	txtgcycl.Text = trim(ds_ogm410.GetColumn(0,"GCYCL"));
	txtgnamt.Text = trim(ds_ogm410.GetColumn(0,"GNAMT"));
	txtjspec.Text = trim(ds_ogm410.GetColumn(0,"JSPEC"));
	txtaccno.Text = trim(ds_ogm410.GetColumn(0,"ACCNO"));

	//if(length(ds_ogm410.GetColumn(0,"DIDAT"))
	txtdidat.Value = trim(ds_ogm410.GetColumn(0,"DIDAT"));
	cmbdresn.Value = trim(ds_ogm410.GetColumn(0,"DRESN"));
	//grboption.Value = trim(ds_ogm410.GetColumn(0,"DPTCD"));
	rdodisyn.Value = trim(ds_ogm410.GetColumn(0,"DISYN"));
	rdoggub.Value =trim(ds_ogm410.GetColumn(0,"G_GUB"));

}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공
	//삭제
	}else if(svcId=="setOgm410del"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	//저장
	}else if(svcId=="setOgm410"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}else if(svcId=="setOgm410_POP"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}

}

