/* --------------------------------------------------------------------
 *교정분야중 "설비" 를 선택했을 때 관리번호 입력창이 2개에서 3개로 변동
 * --------------------------------------------------------------------*/
function grboption_OnClick()
{
	 if(chk_SEL=1){//구관리번호체계
		//adio컨포넌트에서 측정(1) 혹은 설비(2)를 선택했을때 입력폼이 2개로 변경
		if(grboption.Value == "1"){
			// DNPE 처리
			if(G_ds_user.GetColumn(0,"COMPANY") == "PE") {
				txtmngno_pe.Visible = true;
			}
			else {	
				txtmngno_pe.Visible = false;

				txtmngno2.Width ="72";
				txtmngno2.MaxLength="5";
				txtmngno2.Number=true;
				Static_mngno2.Visible = "false";
				txtmngno3.Visible = "false";
				txtmngno1.Text="PSM";
				txtmngno2.Text="";
				txtmngno3.Text="";			
				cmbjkind.Value="";
			}
			var grpcd = "GK1";
			getogmGauge_selmst050(grpcd);
		//adio컨포넌트에서 측정(1) 혹은 설비(2)를 선택했을때 입력폼이 2개로 변경

		} else if(grboption.Value == "2") {
			// DNPE 처리
			if(G_ds_user.GetColumn(0,"COMPANY") == "PE") {
				txtmngno_pe.Visible = true;
			}
			else {	
				txtmngno_pe.Visible = false;

				txtmngno2.Width ="72";
				txtmngno2.MaxLength="5";
				txtmngno2.Number=true;
				Static_mngno2.Visible = "false";
				txtmngno3.Visible = "false";
				txtmngno1.Text="PSG";
				txtmngno2.Text="";
				txtmngno3.Text="";			
				cmbjkind.Value="";
			}
			var grpcd = "GK2";
			getogmGauge_selmst050(grpcd);

		//Radio컨포넌트에서 설비(3)을 선택했을때 입력폼이 3개로 변경 
		} else if(grboption.Value == "3") {
			// DNPE 처리
			if(G_ds_user.GetColumn(0,"COMPANY") == "PE") {
				txtmngno_pe.Visible = true;
			}
			else {	
				txtmngno_pe.Visible = false;

				//Static_mngno1.Visible = "false";
				txtmngno2.Width ="35";
				txtmngno2.MaxLength="1";
				txtmngno2.Number=false;
				Static_mngno2.Visible = "true";
				txtmngno3.Visible = "true";
				txtmngno1.Text="PE";
				txtmngno2.Text="";
				//txtmngno3.Text="";			
				cmbjkind.Value="";
			}
			var grpcd = "GK3";
			getogmGauge_selmst050(grpcd);
		}
	}else{
		//adio컨포넌트에서 측정(1) 혹은 설비(2)를 선택했을때 입력폼이 2개로 변경
		if(grboption.Value == "1"){
			txtmngno_pe.Visible = false;

			txtmngno2.Width ="72";
			txtmngno2.MaxLength="5";
			txtmngno2.Number=true;
			Static_mngno2.Visible = "false";
			txtmngno3.Visible = "false";
			//txtmngno1.Text="PSM";
			//txtmngno1.Text="D"+iif(substr(ComboBoxPlant.Value,0,1)=="C","A",substr(ComboBoxPlant.Value,0,1))+"M";
			txtmngno1.Text="D"+substr(ComboBoxPlant.Value,0,1)+"M";
			txtmngno2.Text="";
			txtmngno3.Text="";			
			cmbjkind.Value="";
			var grpcd = "GK1";
			getogmGauge_selmst050(grpcd);
		//adio컨포넌트에서 측정(1) 혹은 설비(2)를 선택했을때 입력폼이 2개로 변경	
		} else if(grboption.Value == "2") {
			txtmngno_pe.Visible = false;

			txtmngno2.Width ="72";
			txtmngno2.MaxLength="5";
			txtmngno2.Number=true;
			Static_mngno2.Visible = "false";
			txtmngno3.Visible = "false";
			//txtmngno1.Text="PSG";
			txtmngno1.Text="D"+substr(ComboBoxPlant.Value,0,1)+"G";
			txtmngno2.Text="";
			txtmngno3.Text="";			
			cmbjkind.Value="";

			var grpcd = "GK2";
			getogmGauge_selmst050(grpcd);

		//Radio컨포넌트에서 설비(3)을 선택했을때 입력폼이 3개로 변경 
		} else if(grboption.Value == "3") {
			txtmngno_pe.Visible = false;

			//Static_mngno1.Visible = "false";
			txtmngno2.Width ="35";
			txtmngno2.MaxLength="1";
			txtmngno2.Number=false;
			Static_mngno2.Visible = "true";
			txtmngno3.Visible = "true";
			//txtmngno1.Text="PE";
			txtmngno1.Text="D"+substr(ComboBoxPlant.Value,0,1)+"E";
			txtmngno2.Text="";
			//txtmngno3.Text="";
			//txtmngno3.MaxLength="3";
			cmbjkind.Value="";

			var grpcd = "GK3";
			getogmGauge_selmst050(grpcd);
		}
	}
}



