function rdodisyn_OnChanged(obj)
{
	if(rdodisyn.Value == "N"){
		cmbdresn.Value="";
		txtdidat.Value="";
	}
}

function validationChk(){
	 if(chk_SEL=1){//구관리번호체계
	// DNPE 처리
		if(G_ds_user.GetColumn(0,"COMPANY") == "PE") {
			mngno = txtmngno_pe.Text;
			return true;
		}
		else {		

			if(grboption.Value =="1"  || grboption.Value =="2")
			{

				if(length(trim(txtmngno1.Text)) == 0 || length(trim(txtmngno2.Text)) == 0)
				{
					alert(setPopMessage("3058"));
					return false;
				}

				var mngno3 = trim(txtmngno2.Text);
				var mngno3_zero_addcnt = 5 - mngno3.length;
				for(var i = 0 ; i < mngno3_zero_addcnt; i++)
				{
					mngno3 = "0"+mngno3;
				}
				txtmngno2.Text=mngno3;
				mngno = trim(txtmngno1.Text) + trim(txtmngno2.Text);
			}
			else if(grboption.Value =="3")
			{
				if(length(trim(txtmngno1.Text)) == 0 || length(trim(txtmngno2.Text)) == 0 || length(trim(txtmngno3.Text)) == 0)
				{
					alert(setPopMessage("3058"));
					return false;
				}

				mngno = trim(txtmngno1.Text) + trim(txtmngno2.Text) + trim(txtmngno3.Text);
			}
			return true;	
		}
	}else{
		if(grboption.Value =="1"  || grboption.Value =="2")
			{	
			if(length(trim(txtmngno1.Text)) == 0 || length(trim(txtmngno2.Text)) == 0)
				{
					alert(setPopMessage("3058"));
					return false;
				}

				var mngno3 = trim(txtmngno2.Text);
				var mngno3_zero_addcnt = 5 - mngno3.length;
				for(var i = 0 ; i < mngno3_zero_addcnt; i++)
				{
					mngno3 = "0"+mngno3;
				}
				txtmngno2.Text=mngno3;
				mngno = trim(txtmngno1.Text) + trim(txtmngno2.Text);
			}
			else if(grboption.Value =="3")
			{
				if(length(trim(txtmngno1.Text)) == 0 || length(trim(txtmngno2.Text)) == 0 || length(trim(txtmngno3.Text)) == 0)
				{
					alert(setPopMessage("3058"));
					return false;
				}

				mngno = trim(txtmngno1.Text) + trim(txtmngno2.Text) + trim(txtmngno3.Text);
			}
			return true;
	}
}

//폐기일자선택시 자동으로 폐기여부가 Y로 변함.
function txtdidat_OnKillFocus(obj)
{
	if(length(txtdidat.Value) != 0){
		rdodisyn.Value="Y";
	}
}


function setOgm410add() //추가버튼Action ===>추가시 관리번호자동 Counting.
{

	if(chk_SEL=1){//구관리번호체계
		if(G_ds_user.GetColumn(0,"COMPANY") != "PE") {

			if(grboption.Value =="1" || grboption.Value =="2") //측정기,검사구인 경우
			{
				if(length(trim(txtmngno1.Value)) == 0)
				{
					alert(setPopMessage("3059"));
					return false;
				}	
				var headcd = txtmngno1.Text;
				getogm410_maxCnt(headcd);

				var auto_no = toString(parseInt(ds_maxCnt.GetColumn(0,"mngno"))+1);
				//alert(auto_no +"   "+ length(auto_no));		
				var digit_num = 5 - length(auto_no);
				//alert(digit_num);		
				if(digit_num > 0)
				{
					for(var i = 0 ; i < digit_num; i++)
					{
						auto_no = "0" + auto_no ;
					}
				}
				txtmngno2.Text = auto_no;
				alert(setPopMessage("2554"));
			}
			else if(grboption.Value == "3") //설비인 경우
			{
				if(length(trim(txtmngno1.Text)) == 0 || length(trim(txtmngno2.Text)) == 0)
				{
					alert(setPopMessage("2555"));
					return false;
				}	
				var headcd = txtmngno1.Text + txtmngno2.Text;

				getogm410_maxCnt(headcd);

				var auto_no = toString(parseInt(ds_maxCnt.GetColumn(0,"mngno"))+1);
				var digit_num = 3 - length(auto_no);
				if(digit_num > 0)
				{
					for(var i = 0 ; i < digit_num; i++)
					{
						auto_no = "0" + auto_no ;
					}
				}
				txtmngno3.Text = auto_no;
				alert(setPopMessage("2554"));
			}	
			//기존처리대로 개발진행내역  => 설비인 경우 관리번호 자동추가기능이 없음 관리번호를 입력후 중복여부만 체크했었음.
			/*
			else if(grboption.Value == "3") //설비인 경우
			{
				if(length(trim(txtmngno1.Text)) == 0 || length(trim(txtmngno2.Text)) == 0 || length(trim(txtmngno3.Text)) == 0)
				{
					alert(setPopMessage("3059"));
					return false;
				}	
				var mngno = txtmngno1.Text + txtmngno2.Text + txtmngno3.Text;

				//관리번호의 중복을 체크합니다.
				if(getogm410_mngChk(mngno)=="TRUE")
				{
					alert("중복관리번호입니다.");
					//alert(setPopMessage("3058"));
				}
				else
				{
					alert("추가처리 되었습니다.");
					//alert(setPopMessage("3058"));
				}

			}
			*/		
		}
	}else{
		if(grboption.Value =="1" || grboption.Value =="2") //측정기,검사구인 경우
		{
			if(length(trim(txtmngno1.Value)) == 0)
			{
				alert(setPopMessage("3059"));
				return false;
			}	
			var headcd = txtmngno1.Text;
			getogm410_maxCnt(headcd);

			var auto_no = toString(parseInt(ds_maxCnt.GetColumn(0,"mngno"))+1);
			//alert(auto_no +"   "+ length(auto_no));		
			var digit_num = 5 - length(auto_no);
			//alert(digit_num);		
			if(digit_num > 0)
			{
				for(var i = 0 ; i < digit_num; i++)
				{
					auto_no = "0" + auto_no ;
				}
			}
			txtmngno2.Text = auto_no;
			alert(setPopMessage("2554"));
		}
		else if(grboption.Value == "3") //설비인 경우
		{
			if(length(trim(txtmngno1.Text)) == 0 || length(trim(txtmngno2.Text)) == 0)
			{
				alert(setPopMessage("2555"));
				return false;
			}	
			var headcd = txtmngno1.Text + txtmngno2.Text;

			getogm410_maxCnt(headcd);

			var auto_no = toString(parseInt(ds_maxCnt.GetColumn(0,"mngno"))+1);
			var digit_num = 3 - length(auto_no);
			if(digit_num > 0)
			{
				for(var i = 0 ; i < digit_num; i++)
				{
					auto_no = "0" + auto_no ;
				}
			}
			txtmngno3.Text = auto_no;
			alert(setPopMessage("2554"));
		}
	}
	//추가상태인지 조회상태인지를 변수에 저장
	lblNewOrInq.Value = "New";
}

/********************************************************************** 
 * 내용설명 : MaxCnt "SQL_tme010pf_01"
 **********************************************************************/ 
function getogm410_maxCnt(headcd)
{
	var sKind		= "getogm410_maxCnt";						
	var sMethodName = "service::psogmlib/selectOgm410_maxcnt.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_maxCnt=ds_maxCnt";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " HEADCD=" + quote(headcd); //교정분야종류 
		sArgument += " MAXFLAG=" + quote(grboption.Value); //추가내역 장비분류에 따라 자동채번이 달라짐.

	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;
}

function btnCopy_OnClick(obj)
{

	var mngnoPop="";
	var newmngno="";
	if(grboption.Value =="1"  || grboption.Value =="2")
	{
		if(length(trim(txtmngno1.Text)) != 0 && length(trim(txtmngno2.Text)) != 0){
			mngnoPop = trim(txtmngno1.Text) + trim(txtmngno2.Text);
			newmngno = "";
		}
		else {
			mngnoPop = "";
			newmngno = "";
		}
	}
	else if(grboption.Value =="3")
	{
		if(length(trim(txtmngno1.Text)) != 0 && length(trim(txtmngno2.Text)) != 0 && length(trim(txtmngno3.Text)) != 0){
			if(length(trim(txtmngno3.Text)) < 2){
				alert(setPopMessage("2556"));
				return false;
			}
			mngnoPop = trim(txtmngno1.Text) + trim(txtmngno2.Text) + trim(txtmngno3.Text);
			newmngno = getSulbieNextMngno(mngnoPop);		
		}
		else{
			mngnoPop = "";
			newmngno = "";
		}
	}
	poparray[0]= mngnoPop;
	poparray[1]= newmngno;

	var str = dialog("psogmlib::ogm410_POP.xml","팝업",300,300,"TitleBar=false");
	//var str = dialog("psogmlib::ogm410_POP.xml", "dddd",300,300,"TitleBar=false");
}

//설비번호의 끝자리 2자리의 숫자를 다음번호로 만들어주는 함수
//예를 들어 PEZ57-01일 경우 PEZ57-02로 만들어주는 함수
function getSulbieNextMngno(mngnoPop)
{
	var next_mngno="";
	var mngno_length = length(mngnoPop);
	var mngno_root = substr(mngnoPop,0,mngno_length-2);
	var mngno_right_2digit = substr(mngnoPop,mngno_length-2,2);



	if(mngno_right_2digit == "99"){
		var test1 = substr(mngnoPop,mngno_length-3,3);
		var ch =Asc(test1.CharAt(0));

		if(ch  == 45){	
		//alert("-");
			var mngno_next_right_2digit = toString(parseInt(mngno_right_2digit)+1); //정수변환시 05=>5가됨
			next_mngno = mngno_root + mngno_next_right_2digit;
		}
		else{
		//alert("99");		
			var mngno_next_right_2digit = toString(parseInt(test1)+1); //정수변환시 05=>5가됨
			mngno_root = substr(mngnoPop,0,mngno_length-3);
			next_mngno = mngno_root + mngno_next_right_2digit;
		}
	}
	else{
		var mngno_next_right_2digit = toString(parseInt(mngno_right_2digit)+1); //정수변환시 05=>5가됨
		if(mngno_next_right_2digit.length != 2){
		//alert("일반1자리");				
			next_mngno = mngno_root + "0"+mngno_next_right_2digit; //0을 추가시켜줌.
		}
		else{
		//alert("일반2자리");		
			next_mngno = mngno_root + mngno_next_right_2digit;
		}
	}


	/* 수정전.
	var next_mngno="";
	var mngno_length = length(mngnoPop);
	var mngno_root = substr(mngnoPop,0,mngno_length-2);
	var mngno_right_2digit = substr(mngnoPop,mngno_length-2,2);
	var mngno_next_right_2digit = toString(parseInt(mngno_right_2digit)+1); //정수변환시 05=>5가됨.
	if(mngno_next_right_2digit.length != 2){
		next_mngno = mngno_root + "0"+mngno_next_right_2digit; //0을 추가시켜줌.
	}
	else{
		next_mngno = mngno_root + mngno_next_right_2digit;
	}
	*/
	return next_mngno;
}


