			if(length(trim(txtmngno1.Text)) == 0 || length(trim(txtmngno2.Text)) == 0) {
				alert("삭제할 관리번호를 맞게 입력해 주십시요");
				//alert(setPopMessage("3058"));
				return false;
			}
			else {
				mngno = trim(txtmngno1.Text) + trim(txtmngno2.Text);
			}				
		}
		else if(grboption.Value =="3")
		{
			if(length(trim(txtmngno1.Text)) == 0 || length(trim(txtmngno2.Text)) == 0 || length(trim(txtmngno3.Text)) == 0)
			{
				alert("삭제할 관리번호를 맞게 입력해 주십시요");
				//alert(setPopMessage("3058"));
				return false;			
			}
			else {
				mngno = trim(txtmngno1.Text) + trim(txtmngno2.Text) + trim(txtmngno3.Text);				
			}
		}
	*/	
//Trace("mngno:" + mngno);	
		if(getogm410_mngChk(mngno)=="FALSE")
		{
			alert(setPopMessage("2557"));
			btnClear_OnClick();
		}
		else
		{	
			if(trim(lblNewOrInq.Text)=="Inq"){
				var flag101="N";	//chkIsAttme101 존재유무
				var flag100="N";	//isAtTme100 존재유무
				if(chkIsAttme101(mngno)=="TRUE"){
					flag101="Y";
				}
				if(isAtTme100(mngno)=="TRUE"){
					flag100="Y";
				}
				var sKind		= "setOgm410del";						
				var sMethodName = "service::psogmlib/delOgm410.do";     // 서버에서 호출 될 Method 명
				var sInDataSet  = "";               //자료 저장 시 보낼 DataSet 명칭
				var sOutDataSet = "";    							     // 조회 후 돌려 받을 DataSet 명칭
				var sArgument   = "";
					sArgument += " LIB2=" + quote(Lib2);
					sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
					sArgument += " GROUP=" + quote(grboption.Value); //교정분야
					sArgument += " MNGNO=" + quote(mngno); //관리번호
					sArgument += " FALG101=" + quote(flag101); //chkIsAttme101 존재유무
					sArgument += " FALG100=" + quote(flag100); //isAtTme100 존재유무
				http.Sync = true;
				Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
				http.Sync = false;
				btnClear_OnClick();
			}
			else{
				alert(setPopMessage("2509"));
				return false;
			}
		}
	}
	else{
		return;
	}	
}

/********************************************************************** 
 * 내용설명 :  등록 및 수정
 **********************************************************************/ 
function setOgm410()
{
	txtmname.BKColor="white";
	txtipdat.BKColor="white";
	cmbdptcd.BKColor="white";
	txtgcycl.BKColor="white";

	var msg = "장비등록 저장하시겠습니까?. 저장하려면 '확인'을, 취소하려면 '취소'를 클릭하세요.";
	//var mngno = "";
	//if(Confirm(setPopMessage("3001")){
	var msg1="사용부서";
	var msg2="장비명";
	var msg3="교정주기";
	var msg4="입고일자";

	var msgview ="";
	var msgVal=0;

	// DNPE용 수정	
	if(length(trim(txtmname.Text)) == 0 || length(trim(txtipdat.Value)) == 0 || 
		length(trim(cmbdptcd.Value)) == 0 || length(trim(txtgcycl.Text)) == 0)
	{
		if(length(trim(cmbdptcd.Value)) == 0){
			cmbdptcd.BKColor="paleturquoise";
			if(msgVal==0){
				msgview = msg1;	
				msgVal = 1;
			}
		}
		if(length(trim(txtmname.Text)) == 0){
			txtmname.BKColor="paleturquoise";
			if(msgVal==0){
				msgview=msg2;	
				msgVal = 1;
			}
			else {
				msgview = msgview+", " +msg2;
			}
		}
		if(length(trim(txtgcycl.Text)) == 0){
			txtgcycl.BKColor="paleturquoise";
			if(msgVal==0){
				msgview=msg3;	
				msgVal = 1;
			}
			else {
				msgview = msgview+", " +msg3;
			}	
		}
		if(length(trim(txtipdat.Value)) == 0){
			txtipdat.BKColor="paleturquoise";
			if(msgVal==0){
				msgview=msg4;	
				msgVal = 1;
			}
			else {
				msgview = msgview+", " +msg4;
			}
		}
		alert(msgview + "는 반드시 입력해야 합니다.");
		//alert(setPopMessage("3058"));

		return false;
	}

	if(trim(txtgcycl.Value) == 0)
	{
		//txtgcycl.BKColor="paleturquoise";
		//alert("교정주기는 1개월이상으로 입력하세요.");
		alert(setPopMessage("2558"));
		return false;
	}

	if(rdodisyn.Value == "Y")
	{
		if(txtdidat.Value == "" || txtdidat.Value==0 || length(trim(cmbdresn.Value)) == 0)
		{
			//alert("폐기시는 폐기일자와 폐기사유가 반드시 있어야 합니다.");
			alert(setPopMessage("2559"));
			return false;
		}
	}
	if(!validationChk())

		return;

	if(Confirm(msg)){
		//필수항목 체크

		var insFlag ="update";
		var rsign="1"; //폐기여부	
		var rpdat = "0";
		var delFalg ="N";
		var yedat="";
		var didat="";
		if(getogm410_mngChk(mngno)=="FALSE") //추가
		{ //등록인경우
			insFlag ="insert";
			rpdat = addMonth(txtipdat.Value,"-"+parseInt(txtgcycl.Text)); //(예정교정일-주기)로 가상의 최종교정일을 구한다.
			yedat = txtipdat.Value;
			//didat = txtdidat.Value;
			/*
			if(txtdidat.Value !=0 || txtdidat.Value !=""){
				alert("등록시 폐기일자는 입력할수 없습니다.");				
				return false;
			}
			*/
		}
		else { //업데이트인경우
			//if(Confirm("기존에 등록된 관리번호입니다. 변경하시겠습니까?")){
			if(Confirm(setPopMessage("2560"))){
				if(rdodisyn.Value=="Y"){	//폐기저장인경우
					rsign="3"; //폐기여부에 따라 업데이트내역이 달라짐.
					getMaxRpDat(mngno);//최근레코드의 최종교정일자 
					rpdat=ds_maxRpDat.GetColumn(0,"RPDAT");
					didat = txtdidat.Value;
//이상함~~~~~~~~~~~~~~~~~~~~~~`
					if(chkIsAttme101(mngno)=="FALSE"){ //이상함.....데이터가 없는데 삭제처리함.
						delFalg ="Y";					
					} 
				}
				else{//교정이력에서 rsign상태가 '3'인 레코드를 '1'완료상태로 바꾼다.
					rsign="1"; //폐기여부에 따라 업데이트내역이 달라짐.
					getMaxRpDat(mngno);//최근레코드의 최종교정일자 
					didat = "0";
					rpdat=ds_maxRpDat.GetColumn(0,"RPDAT");
					yedat= addMonth(rpdat,"+"+parseInt(txtgcycl.Text)); //(최종교정일+주기)로 가상의 최종교정일을 구한다.
				}
			}
			else {
//Trace("취소");
				return false;
			}

		}
//Trace("등록");

//alert(stdate);
		if(length(trim(txtgnamt.Text))==0){
			txtgnamt.Text="0";
		}				

		var sKind		= "setOgm410";						
		var sMethodName = "service::psogmlib/saveOgm410.do";     // 서버에서 호출 될 Method 명
		var sInDataSet  = "";               //자료 저장 시 보낼 DataSet 명칭
		var sOutDataSet = "";    							     // 조회 후 돌려 받을 DataSet 명칭
		var sArgument   = "";
			sArgument += " LIB2=" + quote(Lib2);
			sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
			sArgument += " GROUP=" + quote(grboption.Value); //교정분야
			sArgument += " MNGNO=" + quote(mngno); //관리번호
			sArgument += " DPTCD=" + quote(cmbdptcd.Value); 
			sArgument += " MNAME=" + quote(trim(txtmname.Text));
			sArgument += " MAKER=" + quote(trim(txtmaker.Text)); 
			sArgument += " MTYPE=" + quote(trim(txtmtype.Text));
			sArgument += " JNBNO=" + quote(trim(txtjnbno.Text));
			sArgument += " JKIND=" + quote(cmbjkind.Value);
			sArgument += " IPDAT=" + quote(txtipdat.Value);
			sArgument += " G_GUB=" + quote(rdoggub.Value);
			sArgument += " J_GUB=" + quote(cmbjgub.Value);
			sArgument += " GCYCL=" + quote(trim(txtgcycl.Value));
			sArgument += " GNAMT=" + quote(trim(txtgnamt.Value));
			sArgument += " JSPEC=" + quote(trim(txtjspec.Text));
			sArgument += " ACCNO=" + quote(trim(txtaccno.Text));
			sArgument += " DISYN=" + quote(rdodisyn.Value);
			sArgument += " DIDAT=" + quote(didat);
			sArgument += " DRESN=" + quote(cmbdresn.Value);
			sArgument += " CNOTE=" + quote(trim(txtcnote.Text));
			sArgument += " ADDAT=" + quote(parseInt(nowDate));
			sArgument += " ADTIM=" + quote(h);
			sArgument += " ADUSR=" + quote(adusr);
			sArgument += " CHDAT=" + quote(parseInt(nowDate));
			sArgument += " CHTIM=" + quote(h);
			sArgument += " CHUSR=" + quote(adusr);

			sArgument += " RPDAT=" + quote(rpdat); //(예정교정일-주기)로 가상의 최종교정일을 구한다.
			//sArgument += " REPNO=" + quote(""); //repno 신규이므로 교정번호가 없다.
			sArgument += " RCYCL=" + quote(trim(txtgcycl.Value)); //gcycl이 rcycle이 된다.
			sArgument += " YEDAT=" + quote(yedat); //입고일자가 예정일자가 된다.
			sArgument += " RSIGN=" + quote(rsign); //rsign 

			sArgument += " INSFLAG=" + quote(insFlag);
			sArgument += " DELFLAG=" + quote(delFalg);


		http.Sync = true;
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
		http.Sync = false;


	}
	else{
		return false;
	}	
}

/********************************************************************** 
 * 내용설명 : 중복체크 "SQL_tme010pf_06" chkMngno
 **********************************************************************/ 
function getogm410_mngChk(mngno)
{
	var sKind		= "getogm410_mngChk";						
	var sMethodName = "service::psogmlib/selectOgm410_mngChk.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_mngChk=ds_mngChk";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " MNGNO=" + quote(mngno); //사업부
		//sArgument += " MNGNO=" + quote("PEA005-01"); //사업부

	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;

	if( ds_mngChk.rowcount() == 0)
	{
		return "FALSE";
	}
	else
	{
		return "TRUE";
	}
}

//tme101pf테이블에 관리번호의 레코드가 존재하는지 체크 SQL_tme101pf_03
function chkIsAttme101(mngno) 
{
	var sKind		= "chkIsAttme101";						
	var sMethodName = "service::psogmlib/selectOgm410_tme101Chk.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_tme101=ds_tme101";
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " GROUP=" + quote(grboption.Value); //교정분야
		sArgument += " MNGNO=" + quote(mngno); //관리번호
		//sArgument += " MNGNO=" + quote("PEA019-02"); //사업부

	http.Sync = true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync = false;

	if( ds_tme101.rowcount() == 0)
	{
		return "FALSE";
	}
	else
	{
		return "TRUE";
	}
}
