	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	// tran 성공	
	}else if(svcId=="setogm200"){
		alert(setPopMessage("1001"));
		setMessage("1001");
	}	
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){

	var result = true;
	var PaValue = txtrpdats.Value;
	var UrValue = txtrpdate.Value;

	if (PaValue == "" || UrValue == "")
	{
		alert(setPopMessage("3048"));
		result = false;
	}
	else if(PaValue > UrValue)
	{
		alert(setPopMessage("3049"));
		result =false;
	}
	return result;
}

