function setExcel()
{
	var fName   = "교정내역조회";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|교정내역조회";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}
function chk_box(){

	var CheckedCount = parseInt(chkgroup1.Value)+parseInt(chkgroup2.Value)+parseInt(chkgroup3.Value);//합이 3
	var rvalue = " (";
	var checking = 0;
	if (chkgroup1.Value == 1){ //ckbBM.Value가 체크되어있으면 1...
		checking = checking+1;
		rvalue = rvalue+"'"+"1"+"'";
	}
	if (chkgroup2.Value == 1){
		if (checking != 0) rvalue = rvalue+",";
		checking = checking+1;
		rvalue = rvalue+"'"+"2"+"'";
	}
	if (chkgroup3.Value == 1){
		if (checking != 0) rvalue = rvalue+",";
		checking = checking+1;
		rvalue = rvalue+"'"+"3"+"'";
	}
	rvalue = rvalue+")";

	var chk_BoxArray = array(2);

	chk_BoxArray[0] = CheckedCount;
	chk_BoxArray[1] = rvalue;

	return chk_BoxArray;
}

function txtrpdats_OnKillFocus(obj)
{
	var rpdats=substr(txtrpdats.Value,0,6)+"01";
	txtrpdats.Value=rpdats;	
}

function txtrpdate_OnKillFocus(obj)
{
	var rpdate = substr(txtrpdate.Value,0,6);
	var rpmdate = addMonth(rpdate+"01", +1); //현재월01일 +1개월
	var rpfdate = addDate(rpmdate, -1);
	txtrpdate.Value=rpfdate;
}

