function MainGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0) lib_grid_SetGridSort(obj,nCell);
}

function MainGrid_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	if (bCtrl == true )
	{ 
		if(nchar == 67)
		{
			ClearClipboard(); // 클립보드를 Clear합니다.
			SetClipBoard("CF_CSV",obj.GetCSVData(true)); // 외부 Excel에 Ctrl+c로 보낼수 있는 예제
		} else if(nChar == 86){

			var dsId = obj.BindDataSet;
			var objDs = object(dsId);

			var clip_txt = GetClipBoard("CF_CSV");

			r = Split2(clip_txt,"\n", ",");

			for (i = 0 ; i < r.length() ; i++)
			{
				c = r[i]; 
				for (j = 0 ; j < c.length(); j++)
				{
					objDs.SetColumn(objDs.Row, "jcode" ,c[j]);
				}
				if(i+1 != r.length()) objDs.addrow(); 
			}
			var objDs = object(dsId);

		}
	}
}


