/********************************************************************** 
 * 내용설명 : 상세화면 조회
 **********************************************************************/ 
function detail_View(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell < 8) return; 
	if(ds_Ogm540.rowcount <= 0 || ds_Ogm540.GetRowType(ds_Ogm540.row) != 'normal') return ;
	var sArgument   = "";
		sArgument += " COMPS=" + quote(ds_Ogm540.GetColumn(ds_Ogm540.row,"COMPS"));
		sArgument += " HOUSE=" + quote(ds_Ogm540.GetColumn(ds_Ogm540.row,"HOUSE"));
		sArgument += " DEVID=" + quote(ds_Ogm540.GetColumn(ds_Ogm540.row,"DEVID"));
		sArgument += " PSPNO=" + quote(ds_Ogm540.GetColumn(ds_Ogm540.row,"PSPNO"));
		sArgument += " PROCS=" + quote(ds_Ogm540.GetColumn(ds_Ogm540.row,"PROCS"));
		var dd;
		if (nCell-7 < 10) {
			dd = "0" + (nCell-7);
		}else{
			dd = nCell-7;
		}
		var ymd = cmb_yyyy.Value + cmb_mm.Value + dd;
		sArgument += " SDATE=" + quote(ymd);
		sArgument += " DEBNM=" + quote(ds_Ogm540.GetColumn(ds_Ogm540.row,"DEBNM"));
		sArgument += " ITDSC=" + quote(ds_Ogm540.GetColumn(ds_Ogm540.row,"ITDSC"));
	//var str = dialog("PSOGMLIB::Ogm540d.xml","COMPS="+ ComboBoxPlant.Value + " " + "GROUP="+ ComboBoxClass.Value + " " + "PRTNO=" + ds_ogm011.GetColumn(ds_ogm011.row,"PRTNO"),1100,600);
	//var str = dialog("PSOGMLIB::Ogm540d.xml","COMPS="+ sComps + " " +"HOUSE=" + sHouse + " " +"DEVID=" + sDevid + " " +"DEBNM=" + sDebnm + " " +"ITDSC=" + sItdsc + " " + "PROCS=" + sProcs + " " + "PSPNO=" + sPspno),1100,600);
	var str = dialog("PSOGMLIB::ogm540d.xml",sArgument,1100,600);
}

