function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	if (COMPS == "C1"){
		lblClass.Text = "PS 창원";
	}else if (COMPS == "H1"){
		lblClass.Text = "PS 홍성";
	}else if (COMPS == "S1"){
		lblClass.Text = "PS 화성";
	}else{
		lblClass.Text = COMPS;
	}
	//lblClass.Text = COMPS;
	lblHOUSE.Text = HOUSE;
	lblDEVID.Text = DEVID;
	lblDEBNM.Text = DEBNM;
	lblDATE.Text = substr(SDATE,0,4)+"년 "+substr(SDATE,4,2)+"월 "+substr(SDATE,6,2)+"일";
	lblDN.Text = DAYGU;

	GetTime(st_time1,st_time2,COMPS,DAYGU);
	var cnt = ds_QRTime.GetRowCount();
	arrTime = Array(cnt);
	var arrTimeE;
	for (var i=0 ; i<cnt ; i++){
		arrTime[i] = ds_QRTime.GetColumn(i,"WRKST").substr(0,2) + ":" + ds_QRTime.GetColumn(i,"WRKST").substr(2,2) + "\n~" + ds_QRTime.GetColumn(i,"WRKED").substr(0,2) + ":" + ds_QRTime.GetColumn(i,"WRKED").substr(2,2) ;
		MainGrid.SetCellProp("Head", i+4, "text", arrTime[i]);
	}

	getogm550d();
}

