function getOgm550d(){
	var sTemp = "";
	var sTemp2 = "";
	var stime = st_time1.Text;
	var etime = st_time2.Text;
	//

	var sKind		= "getOgm550d";						
	var sMethodName = "service::psogmlib/selectOgm550D.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ogm550D=ds_ogm550D";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(COMPS); //사업부
	sArgument += "@HOUSE=" + quote(HOUSE); //그룹
	sArgument += "@DEVID=" + quote(DEVID); //그룹
	sArgument += "@DAYGU=" + quote(DAYGU); //그룹
	sArgument += "@TRNDT=" + quote(SDATE); //그룹
	//1
	sTemp = stime.substr(0,6);
	sTemp2 = etime.substr(0,6);
	divideTime("090000",sTemp,sTemp2,DAYGU);
	sArgument += "@QRST1=" + quote(startTime); //
	sArgument += "@QRED1=" + quote(endTime); //
	//2
	sTemp = stime.substr(6,6);
	sTemp2 = etime.substr(6,6);
	divideTime("090000",sTemp,sTemp2,DAYGU);
	sArgument += "@QRST2=" + quote(startTime); //
	sArgument += "@QRED2=" + quote(endTime); //
	//3
	sTemp = stime.substr(12,6);
	sTemp2 = etime.substr(12,6);
	divideTime("090000",sTemp,sTemp2,DAYGU);
	sArgument += "@QRST3=" + quote(startTime); //
	sArgument += "@QRED3=" + quote(endTime); //
	//4
	sTemp = stime.substr(18,6);
	sTemp2 = etime.substr(18,6);
	divideTime("090000",sTemp,sTemp2,DAYGU);
	sArgument += "@QRST4=" + quote(startTime); //
	sArgument += "@QRED4=" + quote(endTime); //
	//5
	sTemp = stime.substr(24,6);
	sTemp2 = etime.substr(24,6);
	divideTime("090000",sTemp,sTemp2,DAYGU);
	sArgument += "@QRST5=" + quote(startTime); //
	sArgument += "@QRED5=" + quote(endTime); //
	//6
	sTemp = stime.substr(30,6);
	sTemp2 = etime.substr(30,6);
	divideTime("090000",sTemp,sTemp2,DAYGU);
	sArgument += "@QRST6=" + quote(startTime); //
	sArgument += "@QRED6=" + quote(endTime); //
	//7
	sTemp = stime.substr(36,6);
	sTemp2 = etime.substr(36,6);
	divideTime("100000",sTemp,sTemp2,DAYGU);
	sArgument += "@QRST7=" + quote(startTime); //
	sArgument += "@QRED7=" + quote(endTime); //
	//8
	sTemp = stime.substr(42,6);
	sTemp2 = etime.substr(42,6);
	divideTime("100000",sTemp,sTemp2,DAYGU);
	sArgument += "@QRST8=" + quote(startTime); //
	sArgument += "@QRED8=" + quote(endTime); //
	//9
	sTemp = stime.substr(48,6);
	sTemp2 = etime.substr(48,6);
	divideTime("100000",sTemp,sTemp2,DAYGU);
	sArgument += "@QRST9=" + quote(startTime); //
	sArgument += "@QRED9=" + quote(endTime); //
	//10
	sTemp = stime.substr(54,6);
	sTemp2 = etime.substr(54,6);
