function divideTime(sbase,sTemp,sTemp2,sDaygu){
	var d2 = 240000;
	if( sTemp < sbase && sDaygu == 'N' ){
		startTime = d2 + toNumber(sTemp);
	}else{
		startTime = sTemp;
	}
	if( sTemp2 < sbase && sDaygu == 'N' ){
		endTime = d2 + toNumber(sTemp2);
	}else{
		endTime =  sTemp2;
	}
}

