function on_LoadCompleted(obj)
{
	//ed_select.Text = CODE;
	//if (CODE == "zzzzzzzzzz") ed_select.Text="";
	switch (SEL){
		case 1 : 
			title_info.Text = "사용자선택"; 
			st_title1.Text = "부서";
			st_title2.Text = "사번";
			http.Sync 	= false;
			var sKind		= "getOGMPrtcd";		
			var sMethodName = "service::psogmlib/selectOGMPrtcd.do";  					
			var sInDataSet  = "";
			var sOutDataSet = "ds_Bsel=ds_Select";
			var sArgument   = "";
			sArgument += " LIB2=" + quote(Lib2);
			sArgument += " COMPS=" + quote(COMPS); //사업부
			sArgument += " GROUP=" + quote(GROUP); //그룹
			Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
			break;
		case 2 : title_info.Text = "?"; break;  //향후 추가를 위하여
		case 3 : title_info.Text = "?"; break;  //향후 추가를 위하여 남겨둠
		case 4 : title_info.Text = "?"; break;  //향후 추가를 위하여 남겨둠
		case 5 : title_info.Text = "?"; break;  //향후 추가를 위하여 남겨둠
		case 6 : title_info.Text = "?"; break;  //향후 추가를 위하여 남겨둠
		case 7 : title_info.Text = "?"; break;  //향후 추가를 위하여 남겨둠
		case 8 : title_info.Text = "?"; break;  //향후 추가를 위하여 남겨둠
		case 9 : title_info.Text = "?"; break;  //향후 추가를 위하여 남겨둠
		case 10 : title_info.Text = "?"; break;  //향후 추가를 위하여 남겨둠
		default : title_info.Text = "";
	}

	//imgQuery_OnClick();
}

