function imgQuery_OnClick(obj,nX,nY)
{
	var iSel = SEL;
	//title_user.Value = USER_ID;
	if (iSel== 1){   //부서명으로 사번찾기
		http.Sync 	= false;
		var sKind		= "getOGMBuSabun";		
		var sMethodName = "service::psogmlib/selectOGMBuSabun.do";  					
		var sInDataSet  = "";
		var sOutDataSet = "ds_Select=ds_Select";
		var sArgument   = "";
		    sArgument += " LIB2=" + quote(Lib2);
		    sArgument += " LIB3=" + quote(Lib3);
		    sArgument += " COMPS=" + quote(COMPS); //사업부
		    sArgument += " DPCODE=" + quote(ComboBox.Value); //부서코드
		    sArgument += " SABUN=" + quote(ed_select.Text); //사번
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	}
	if (iSel== 2){   //
		//
	}
	if (iSel== 3){   //
		//
	}
	if (iSel== 4){   //
		//
	}
	if (iSel== 5){   //
		//
	}
	if (iSel== 6){   //
		//
	}
}

/********************************************************************** 
 * 개 발 자 : 이상건
 * 개발일자 : 2011-08-25
 * 내용설명 : 선택
 * 주요변수 : 
 **********************************************************************/ 
function imgSelect_OnClick(obj,nX,nY)
{
	var arrReturn  =  Array();
	arrReturn[0]  = ds_Select.GetColumn(ds_Select.row,"A1");
	arrReturn[1]  = ds_Select.GetColumn(ds_Select.row,"A2");
	arrReturn[2]  = ds_Select.GetColumn(ds_Select.row,"A3");
	close(arrReturn);
}

function menuGrd2_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	imgSelect_OnClick();
}
