function cb_searchCodeName_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == "PS"){
		LIB1 = "PSRESLIB";
		LIB2 = "PSCDBLIB";
	} else if(strCode == "PE"){
		LIB1 = "PERESLIB";
		LIB2 = "PSEDBLIB";
	}
	ds_R_BHZE208Q.ClearData();
}
