var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

var next_month;

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("1"); //조회, 추가, 저장
	getComCode(); //공장구분 선택

}

function getComCode(){
	// KR용 영업지원시스템 : KA,KE사업부
	setCompanyName(ds_ComCode_PLNT);
	ComboBoxPlant.Value="PS";
	if (ComboBoxPlant.Value == "PS"){
		Lib1 = "PSRESLIB";
		Lib2 = "PSCDBLIB";
	} else if(ComboBoxPlant.Value == "PE"){
		Lib1 = "PERESLIB";
		Lib2 = "PSEDBLIB";
	}
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.UserData = MainGrid.Height;

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	

	//Static0.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");
	//Static3.BkColor = G_ds_design.GetColumn(0,"BTBkColor2");
}

function getR_FMZZ100Q(){

	if(!validationChk()){
		return;
	}

	var p_pspno1,p_pspno2,p_pnoyn;
	p_pspno1 = iif(length(trim(ed_PSPNO_F.Text)) == 0 , '', trim(ed_PSPNO_F.Text));
	p_pspno2 = iif(length(trim(ed_PSPNO_T.Text)) == 0 , '999999999999999', trim(ed_PSPNO_T.Text));
	p_pnoyn = iif(length(trim(ed_PSPNO_F.Text)) == 0 && length(trim(ed_PSPNO_T.Text)) == 0 , 'N', 'Y');

	var sKind		= "getR_FMZZ100Q";						
	var sMethodName = "service::psfaclib/selectR_FMZZ100Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                            				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_R_FMZZ100Q=ds_R_FMZZ100Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@TRYYM=" + quote(substr(cal_TRYYM.Value,0,6)); //실적년월
		sArgument += "@PNOYN=" + quote(p_pnoyn);
		sArgument += "@PSPNO1=" + quote(p_pspno1);
		sArgument += "@PSPNO2=" + quote(p_pspno2);
	var str = dialog("Common::PGMLOAD_FAC.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	if(cal_TRYYM.Value == ""){
		alert(setPopMessage("6062"));  //[알림]  입력하세요.
		setMessage("6062");
		result = false;
	}
	return result;
}
/********************************************************************** 
 * 내용설명 : 상위메뉴 변경시 초기화
 **********************************************************************/ 
function common_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_R_FMZZ100Q.ClearData();
}
function ComboBoxPlant_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == "PS"){
		LIB1 = "PSRESLIB";
		LIB2 = "PSCDBLIB";
	} else if(strCode == "PE"){
		LIB1 = "PERESLIB";
		LIB2 = "PSEDBLIB";
	}
	ds_R_FMZZ100Q.ClearData();
}
/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = "생산실적현황";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|생산실적현황";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

/********************************************************************** 
 * 내용설명 : Sort
 **********************************************************************/ 
function MainGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0) lib_grid_SetGridSort(obj,nCell);
}

/********************************************************************** 
 * 내용설명 : 복사
 **********************************************************************/ 
function MainGrid_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	// Ctrl + C
	if(bCtrl == 1 && nChar == 67) {
		// grd_Standard.AreaSelect = true;
		grd_fn_ClipboardCopy(obj, strSeparator);
	}
}

