var nowDate = today();


/********************************************************************** 
 * 내용설명 : 화면로드
 * 주요변수 : 
 **********************************************************************/ 
function on_LoadCompleted(obj){
	getTitle();	
	getDesign();	
	getComCode();
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부  1:조회,excel                1.1:조회,발행,excel                 1.2:조회     
	                          //                       2.1:조회,추가,삭제,저장     2.2:조회,추가,삭제,저장,excel       2.3:조회,저장,excel
	                          //                       2.4:조회,추가,저장          2.5:저장                            2.6:조회,삭제,저장
	                          //                       2.7:조회,추가,저장,excel 	
	cal_PLNYM.Value = nowDate;
	var ym0 = substr(cal_PLNYM.Value,0,4)+"/"+substr(cal_PLNYM.Value,4,2);
	var ym1 = substr(AddMonth(cal_PLNYM.Value,1),0,4)+"/"+substr(AddMonth(cal_PLNYM.Value,1),4,2);
	var ym2 = substr(AddMonth(cal_PLNYM.Value,2),0,4)+"/"+substr(AddMonth(cal_PLNYM.Value,2),4,2);
	var ym3 = substr(AddMonth(cal_PLNYM.Value,3),0,4)+"/"+substr(AddMonth(cal_PLNYM.Value,3),4,2);
	var ym4 = substr(AddMonth(cal_PLNYM.Value,4),0,4)+"/"+substr(AddMonth(cal_PLNYM.Value,4),4,2);
	var ym5 = substr(AddMonth(cal_PLNYM.Value,5),0,4)+"/"+substr(AddMonth(cal_PLNYM.Value,5),4,2);
	var ym6 = substr(AddMonth(cal_PLNYM.Value,6),0,4)+"/"+substr(AddMonth(cal_PLNYM.Value,6),4,2);
	MainGrid.SetCellProp("Head", 8, "text",ym0);
	MainGrid.SetCellProp("Head", 9, "text",ym1);
	MainGrid.SetCellProp("Head", 10, "text",ym2);
	MainGrid.SetCellProp("Head", 11, "text",ym3);
	MainGrid.SetCellProp("Head", 12, "text",ym4);
	MainGrid.SetCellProp("Head", 13, "text",ym5);
	MainGrid.SetCellProp("Head", 14, "text",ym6);
	MainGrid.UserData = MainGrid.Height;
}


/********************************************************************** 
 * 내용설명 : 공통코드 조회
 * 주요변수 : 
 **********************************************************************/ 
function getDesign(){
//	MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid.Top = G_ds_design.GetColumn(0,"Top");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");		
}

function getComCode(){
	// KR용 영업지원시스템 : KA,KE사업부
	setCompanyName(ds_ComCode_PLNT);
	cb_searchCodeName.Value="PS";
	if (cb_searchCodeName.Value == "PS"){
		Lib1 = "PSRESLIB";
		Lib2 = "PSCDBLIB";
	} else if(cb_searchCodeName.Value == "PE"){
		Lib1 = "PERESLIB";
		Lib2 = "PSEDBLIB";
	}

}


/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getR_SPMZ111Q(){
	var p_pyvnd1,p_pyvnd2,p_plngb;
	p_pyvnd1 = iif (length(trim(ed_pyvnd_from.Text)) == 0, '0000', trim(ed_pyvnd_from.Text));
	p_pyvnd2 = iif (length(trim(ed_pyvnd_to.Text)) == 0 , '9999', trim(ed_pyvnd_to.Text));

	var sKind		= "getR_SPMZ111Q";						
	var sMethodName = "service::pssallib/selectR_SPMZ111Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_R_SPMZ111Q=ds_R_SPMZ111Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@PLNYM=" + quote(substr(cal_PLNYM.Value,0,6));
	sArgument += "@PYVND1=" + quote(p_pyvnd1);
	sArgument += "@PYVND2=" + quote(p_pyvnd2);	
	str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	

	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
	ed_pyvnd_from.SetFocus();
}


/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "월판매계획현황";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|월판매계획현황";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}
}

function Grid0_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if(nCell != 0) lib_grid_SetGridSort(obj,nCell);
}

function Grid0_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	// Ctrl + C
	if(bCtrl == 1 && nChar == 67) {
		grd_fn_ClipboardCopy(obj, strSeparator);
	}
}

