function getSALE154P(){

	if(!validationChk()){
		return;
	}
	var sVnmVal="";
	var eVnmVal="";
	var ePrtNoVal="";
	var sDateVal="";
	var eDateVal="";
	if(length(ed_SVNM.Text)==0){
		sVnmVal="00000000";
	}
	else{
		sVnmVal=ed_SVNM.Text;
	}	
	if(length(ed_EVNM.Text)==0){
		eVnmVal="00007999";
	}
	else{
		eVnmVal=ed_EVNM.Text;
	}		
	if(length(ed_EPRTNO.Text)==0){
		ePrtNoVal="999999999999999";
	}
	else{
		ePrtNoVal=ed_EPRTNO.Text;
	}		
	if(cmb_SDATE.Value == ""){
		sDateVal="00000000";
	}
	else if(cmb_SDATE.Value != ""){
		sDateVal=cmb_SDATE.Value;
	}
	if(cmb_EDATE.Value == ""){
		eDateVal="99999999";
	}
	else if(cmb_EDATE.Value != ""){
		eDateVal=cmb_EDATE.Value;
	}	
	var sKind		= "getSALE154P";						
	var sMethodName = "service::pssallib/selectSALE154P.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SALE154=ds_SALE154";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@SVNM=" + quote(sVnmVal); //업체
	sArgument += "@EVNM=" + quote(eVnmVal); //업체
	sArgument += "@SPRTNO=" + quote(ed_SPRTNO.Text); //품번
	sArgument += "@EPRTNO=" + quote(ePrtNoVal); //품번
	sArgument += "@SDATE=" + quote(sDateVal); //적용일
	sArgument += "@EDATE=" + quote(eDateVal); //적용일
	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
	//http.Sync=true;
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	//http.Sync=false;
	if (str == 'T'){  //정상조회	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
	//Ubi0.setJrfFileDir("http://10.74.139.50/ui/PSSALLIB/REPORT/");
	//Ubi0.setJrfFileDir("http://10.72.209.23/ui/PSSALLIB/REPORT/");
	Ubi0.setJrfFileDir(G_baseURL+"ui/PSSALLIB/REPORT/");
	Ubi0.setJrfFileName("sale154_rep.jrf");
	Ubi0.SetDataset("ds_sale154_rep", "ds_SALE154");
	Ubi0.Retrieve();
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){

	var result = true;
	var sdVal = cmb_SDATE.Value;
	var edVal = cmb_EDATE.Value;

	if (sdVal == "" || edVal == "")
	{
		alert(setPopMessage("3048"));
		result = false;
	}
	else if(sdVal > edVal)
	{
		alert(setPopMessage("3049"));
		result =false;
	}
	return result;
}

