function setExcel()
{
	var fName   = "제품단가등록확인서";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|제품단가등록확인서";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

