 * 내용설명 : 상위메뉴 변경시 초기화
 **********************************************************************/ 
function common_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_SALE201.DeleteAll();
}

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 **********************************************************************/ 
function setExcel()
{
	var fName   = '\\' + "출하정보TABLE마스터";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|출하정보TABLE마스터";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}

