}

function fn_Search()
{
	getSALE201();
}

function fn_Input()
{
	if(checkInsertCount("ds_SALE201")== false) return;
	if(ds_SALE201.rowcount < 1) fn_Search();  
	ds_SALE201.AddRow();
	ds_SALE201.SetColumn(ds_SALE201.currow, "COMPS", comps);
	ds_SALE201.SetColumn(ds_SALE201.currow, "PYVND", ed_PYVND.Text);	
}

function fn_Delete()
{
	if(ds_SALE201.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_SALE201.DeleteRow(ds_SALE201.currow);	//삭제		
	alert(setPopMessage("1011"));				//[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.		
}

function fn_Save()
{
	setSALE201();
}

function fn_print()
{
	alert("fn_print");
}

function fn_Excel()
{
	setExcel();
}

function fn_Help()
{
	alert(setPopMessage("9999"));
	setMessage("9999");
}

function menu_OnFocus(obj)
{
	gFcOnTopButtonColor("2.2"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)
}

function ds_SALE201_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if( strColumnID == "CARCD" ){
		if(varNewValue != "" ){
			tfalg="CAR";
			var tval=getMST050CHK(varNewValue);
			if(tval=="0"){
				alert("차종을 확인해주세요.");
				return false;
			}
		}
	}	
}

/********************************************************************** 
 * 내용설명 : 구분 값이 0,1이 아닐경우 이동불가
 **********************************************************************/ 

function imgMaker_OnClick(obj,nX,nY)
{
	var sTemp=trim(ed_PYVND.Text);
	if (length(trim(ed_PYVND.Text)) == 0) sTemp="zzzzzzzzzz";
	var selectnumber = "B0";
	var rtn = dialog("Common::commonPopup.xml","SEL=" + selectnumber + " " + "CODE=" + sTemp,584,528);
	if (length(rtn) != 0){
		ed_PYVND.Text = rtn[0];
		st_PYVND.Text = rtn[1];
	}
}

function ed_PYVND_OnCharChanged(obj,strPreText,strPostText)
{
	st_PYVND.Text = "";
}

function ed_PYVND_OnChanged(obj,strText)
{
	ds_SALE201.ClearData();
}

function ed_PYVND_OnKillFocus(obj)
{
	if(length(trim(obj.Text)) < 1) return;
	//득의선Check
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getOGMPrtno";		
	var sMethodName = "service::pscomlib/selectCUSMAS1.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_CUSMAS1=ds_CUSMAS1";
	var sArgument   = "";
		sArgument  += " LIB1=" + quote(Lib1);
		sArgument  += " CUSNO=" + quote(ed_PYVND.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_CUSMAS1.GetRowCount() < 1){
		alert(setPopMessage("1502"));
		setMessage("1502");	
		ed_PYVND.SetFocus();
		return;
	}
	st_PYVND.Text=ds_CUSMAS1.GetColumn(0,"CUSNM");
	http.usewaitcursor = true;	
}

//전체삭제
function Button3_OnClick(obj)
{
	if(ds_SALE201.rowcount < 1) {			//삭제가 없으면 건너뛰기
		return;
	}
	if(length(ed_PYVND.Text)==0) {
		return;
	}	
	ds_SALE201.DeleteAll();	//전체삭제		
	alert(setPopMessage("1011"));	
}

function getMST050CHK(tval){

	var sKind		= "getMST050CHK";						
	var sMethodName = "service::pssallib/selectMST050_MRP.do";	// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             			// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MST050=ds_MST050";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " TFLAG=" + quote(tfalg); //체크구분
		//sArgument += " TVAL=" + quote(obj.Text); 
		sArgument += " TVAL=" + quote(tval); 

	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;

	if(ds_MST050.rowcount < 1){
		return 0;	
	}
	else{
		return 1;
	}
}

function getMST100CHK(carcd, bdycd){

	var sKind		= "getMST100CHK";						
	var sMethodName = "service::pssallib/selectMST100_MRP.do";	// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             			// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_MST100=ds_MST100";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " CARCD=" + quote(carcd); //체크구분
		sArgument += " BDYCD=" + quote(bdycd); 

	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;

	if(ds_MST100.rowcount < 1){
		return 0;	
	}
	else{
		return 1;
	}
}
function ed_PYVND_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar != 13){
		return;
	}
	getSALE201();	
}
