function getSalz100(){

	if(!validationChk()){
		return;
	}
	var makdtVal = substr(cmb_MAKDT.Value,0,4);
	var qryVal = makeQry(makdtVal);

	var sKind		= "getSalz100";						
	var sMethodName = "service::pssallib/selectSALZ100.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SALZ100=ds_SALZ100";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		//sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@PYVND=" + quote(ed_PYVND.Text); //득의선
		sArgument += "@MAKDT=" + quote(makdtVal); //년도
		sArgument += "@SQRY=" + quote(qryVal); //쿼리
	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
	autoNum=0;	//초기화
	ds_SALZ100_INPUT.ClearData();
}

/********************************************************************** 
 * 내용설명 : insert, update, delete
 **********************************************************************/ 

 //등록시 업체코드 존재유무 체크함. 없으면 동록불가.
function setSalz100(){

	if(length(trim(st_PYVND.Text)) < 1){
		alert(setPopMessage("4674"));
		setMessage("4674");	
		ed_PYVND.SetFocus();
		return;
	}	
	if(cmb_MAKDT.Value==""){
		alert(setPopMessage("4029"));
		setMessage("4029");	
		cmb_MAKDT.SetFocus();
		return;
	}		
	var makdtVal = substr(cmb_MAKDT.Value,0,4);

	var sKind		= "setSalz100";						
	var sMethodName = "service::pssallib/insertSALZ100.do";    // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_SALZ100_INPUT=ds_SALZ100_INPUT:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SALZ100_CHECK=ds_SALZ100_CHECK";  	   // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " SCOMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " SPYVND=" + quote(ed_PYVND.Text); //업체
http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
http.Sync=false;
	autoNum=0;	//초기화
	ds_SALZ100_INPUT.ClearData();

}
/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_SALZ100_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_SALZ100_CHECK.GetRowCount();i++){
			alert("일자 : " +ds_SALZ100_CHECK.GetColumn(i,"PYVND") + " = " + ds_SALZ100_CHECK.GetColumn(i,"RTMSG"));
		}
		getSalz100();  //error항목이 존재시에는 재조회
		return;
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else 
	{
		//getSalz100();  //error항목이 존재시에는 재조회
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){

	var result = true;
	var pyvndVal = st_PYVND.Text;
	var makdtVal = cmb_MAKDT.Value;
		ed_PYVND.BKColor="white";
		cmb_MAKDT.BKColor="white";

	if (pyvndVal == "")
	{
		ed_PYVND.BKColor="paleturquoise";
		//alert("득의선을 입력하세요.");
		alert(setPopMessage("4674"));
		result = false;
	}
	else if (makdtVal == "")
	{
		cmb_MAKDT.BKColor="paleturquoise";
		//alert("년도를 입력하세요.");
		alert(setPopMessage("4029"));
		result = false;
	}	
	return result;
}

