function ed_PYVND_OnKillFocus(obj)
{
	if(length(trim(obj.Text)) < 1) return;
	//득의선Check
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getOGMPrtno";		
	var sMethodName = "service::pscomlib/selectCUSMAS1.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_CUSMAS1=ds_CUSMAS1";
	var sArgument   = "";
		sArgument  += " LIB1=" + quote(Lib1);
		sArgument  += " CUSNO=" + quote(ed_PYVND.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_CUSMAS1.GetRowCount() < 1){
		alert(setPopMessage("1502"));
		setMessage("1502");	
		ed_PYVND.SetFocus();
		return;
	}
	st_PYVND.Text=ds_CUSMAS1.GetColumn(0,"CUSNM");
	http.usewaitcursor = true;	
}

/*
< '-':1 일 , 'T':0.5 일 ,'S': 일요일 , 'H': 휴일 '>
*/
//쿼리문 생성
function makeQry(makdtVal){

	var mqry="";
	for( j=1; j<32; j++){
		if(length(mqry) != 0){
			mqry = mqry +",";
		}
		mqry = mqry +"MAX(CASE WHEN SUBSTR(MAKDT,7,2)="+lpad(j,0,2)+" THEN MAKMK ELSE  '' END) AS DAY"+j;
	}	
	return mqry;	
}

//선택된일자의 작업일 존재 확인
function getSAL100MST(smakdt){

	var sKind		= "getSAL100CHK";						
	var sMethodName = "service::pssallib/selectSAL100_MST.do";	// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SAL100MST=ds_SAL100MST";    	// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " PYVND=" + quote(ed_PYVND.Text); //득의선
		sArgument += " MAKDT=" + quote(smakdt); //체크구분

	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;

	if(ds_SAL100MST.rowcount < 1){
		return 0;	//작업일이 없으면 0
	}
	else{
		return 1;	//작업일이 있으면 1
	}		
}

//입력시 H,S,T,-만 입력가능
function ds_SALZ100_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	if(strColumnID!="SUMDAY"){
		if(toUpper(varOldValue)!=toUpper(varNewValue)){
			var tChk =(ds_SALZ100.GetColumn(nRow, "MDT")+lpad(substr(strColumnID,3,2),0,2));
			var chlChk=getSAL100MST(tChk); //작업일 체크
			if(chlChk == 1){ //작업일 OK
				if(toUpper(varNewValue) != "H" && toUpper(varNewValue) != "S" && varNewValue != "-" && toUpper(varNewValue) != "T" ){
					alert(setPopMessage("4683"));
					return false;
				}
			}
			else{//작업일 NG
					alert(setPopMessage("4684"));
				return false;
			}
			ds_SALZ100_INPUT.InsertRow(autoNum);		
			ds_SALZ100_INPUT.SetColumn(autoNum,"SMAKDT",ds_SALZ100.GetColumn(nRow, "MDT")+lpad(substr(strColumnID,3,2),0,2));			
			ds_SALZ100_INPUT.SetColumn(autoNum,"SMAKMK",toUpper(varNewValue));
	//alert(ds_SALZ100_INPUT.GetColumn(autoNum,"SMAKDT")+"=="ds_SALZ100_INPUT.GetColumn(autoNum,"SMAKMK"));	
			autoNum++;
//alert(autoNum);
		}
	}
/*
	if(strColumnID!="SUMDAY"){
		if(varOldValue!=varNewValue){
			var tChk =(ds_SALZ100.GetColumn(nRow, "MDT")+lpad(substr(strColumnID,3,2),0,2));
			var chlChk=getSAL100MST(tChk); //작업일 체크
			if(chlChk == 1){ //작업일 OK
