//작업일수 합계 
function ds_SALZ100_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID!="SUMDAY" || ds_SALZ100.GetRowType(ds_SALZ100.currow)=="update" ||ds_SALZ100.GetRowType(ds_SALZ100.currow)=="insert" ){
		for( i=1; i <32; i++){	
			if( strColumnID == "DAY"+i ){
				if(varOldValue!=varNewValue){
					ds_SALZ100.SetColumn(nRow,"DAY"+i, toUpper(varNewValue));
					var dVal = "";
					var dSum = 0;
					for( j=1; j <32; j++){
						jsum = ds_SALZ100.GetColumn(ds_SALZ100.currow,"DAY"+j);
						//alert(jsum+"  줄"+i);
						if(jSum =="-"){
							dSum = dSum + 1;
						}
						else if(jSum =="T"){
							dSum = dSum + 0.5;
						}
					}
					ds_SALZ100.SetColumn(ds_SALZ100.currow,"SUMDAY",dSum);
				}
			}
		}
	}
/*
	if(strColumnID!="SUMDAY" || ds_SALZ100.GetRowType(ds_SALZ100.currow)=="update" ||ds_SALZ100.GetRowType(ds_SALZ100.currow)=="insert" ){
