function fn_Input()
{

	if(!validationChk()){
		return;
	}
	//if(checkInsertCount("ds_SALZ101")== false) return;
	if(ds_SALZ101.rowcount < 1) fn_Search();
	if(ds_SALZ101.rowcount < parseInt(lastDay)){
		ds_SALZ101.AddRow();
		var P_MAKDD="";
		var P_MAKDT="";
		P_MAKDD = lpad(ds_SALZ101.row+1,"0",2);
		P_MAKDT = substr(cmb_MAKYM.Value,0,6)+P_MAKDD;

		ds_SALZ101.SetColumn(ds_SALZ101.currow,"COMPS",ComboBoxPlant.Value);
		ds_SALZ101.SetColumn(ds_SALZ101.currow,"PYVND",ed_PYVND.Text);
		ds_SALZ101.SetColumn(ds_SALZ101.currow,"MAKDT",P_MAKDT);
		ds_SALZ101.SetColumn(ds_SALZ101.currow,"MAKYY",substr(cmb_MAKYM.Value,0,4));
		ds_SALZ101.SetColumn(ds_SALZ101.currow,"MAKYM",substr(cmb_MAKYM.Value,0,6));
		ds_SALZ101.SetColumn(ds_SALZ101.currow,"MAKDD",P_MAKDD);
		ds_SALZ101.SetColumn(ds_SALZ101.currow,"CLHCD",0);
	}else{
		//alert("[알림]해당년월의 일자만큼 등록되어 더이상 등록할 수 없습니다.");
		alert(setPopMessage("6098"));//[알림]해당년월의 일자만큼 등록되어 더이상 등록할 수 없습니다.
		setMessage("6098");
	}
}

