{
	if(nChar == 13){
		fn_Search();
	}
}

//마지막일계산
function getLastday(){

	var year = parseInt(substr(cmb_MAKYM.Value,0,4));
	var month = substr(cmb_MAKYM.Value,4,2);
	var end_day = "";

	if (month=="01" || month=="03" || month=="05" || month=="07" || month=="08" || month=="10" || month=="12") {
		end_day = "31";
	}else if (month=="04" || month=="06" || month=="09" || month=="11") {
		end_day = "30";
	}else if (month=="02" && year%4 == 0) {
		end_day = "29";
	}else if (month=="02" && year%4 != 0) {
		end_day = "28";
	}
	lastDay=end_day;
}


function ds_SALZ101_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID=="MAKDD"){
		if(varNewValue!=""){
			if(ds_SALZ101.FindRow("MAKDD",varNewValue)!="-1"){
				//alert("[경고] 이미 등록된 자료입니다.");  //[경고] 이미 등록된 자료입니다.
				alert(setPopMessage("1520"));  //[경고] 이미 등록된 자료입니다.
				setMessage("1520");
				ds_SALZ101.DeleteRow(nRow);
				return false;
			}
		}
	}	

	if(strColumnID=="MAKMK"){
		if(toUpper(varNewValue) != "H" && toUpper(varNewValue) != "S" && varNewValue != "-" && toUpper(varNewValue) != "T" ){
			//alert("[알림](ㅡ : 작업일  T : 토요일 S : 일요일 H : 공휴일)만 입력가능합니다.");
			alert(setPopMessage("4683"));//[알림](ㅡ : 작업일  T : 토요일 S : 일요일 H : 공휴일)만 입력가능합니다.
			setMessage("4683");				
			return false;
		}
	}
	if(strColumnID=="CLHCD"){
		if(varNewValue != "0" && varNewValue != "1"){
			//alert("[알림]출하코드는 (0,1)만 입력가능합니다.");
			alert(setPopMessage("4685"));//[알림]출하코드는 (0,1)만 입력가능합니다.
			setMessage("4685");				
			return false;
		}
	}

	if(strColumnID=="MLTCD"){
		if(varNewValue!=""){
			if(ds_SALZ101.FindRow("MLTCD",varNewValue)!="-1"){
				//alert("[경고] 이미 등록된 자료입니다.");  //[경고] 이미 등록된 자료입니다.
				alert(setPopMessage("1520"));  //[경고] 이미 등록된 자료입니다.
				setMessage("1520");	
				return false;		
			}

		}
	}						
}

function ds_SALZ101_CanRowPosChange(obj,nOldRow,nRow)
{
	if(ds_SALZ101.GetRowType(ds_SALZ101.currow)=="update" ||ds_SALZ101.GetRowType(ds_SALZ101.currow)=="insert" ){
		var p_MAKDT=ds_SALZ101.GetColumn(ds_SALZ101.currow,"MAKDT");
		var p_MAKMK=ds_SALZ101.GetColumn(ds_SALZ101.currow,"MAKMK");

		if(length(p_MAKDT)!=0){
			if(length(p_MAKMK)==0){
				//alert("[알림]코드를 입력하세요.");
				alert(setPopMessage("6102")); //[알림]코드를 입력하세요.
				setMessage("6102");	
				return false;
			}			
		}
		return true;
	}
}
