	MainGrid.UserData = MainGrid.Height;
	//ComboBoxPlant.Value="C1";
	//cmb_JSOYY.Value="20120101";
	//ed_PYVND.Text="1100";
	//ed_PSPNO.Text='33902-40000H';
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	//ComboBoxPlant.Enable = false; 
}
function getDesign(){
	MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

function getSJSZ100Q(){

	if(!validationChk()){
		return;
	}
	var sKind		= "getSJSZ100Q";						
	var sMethodName = "service::pssallib/selectSJSZ100Q.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SJSZ100Q=ds_SJSZ100Q";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += "@JSOYY=" + quote(substr(cmb_JSOYY.Value,0,4)); //적송년
	sArgument += "@PYVND=" + quote(ed_PYVND.Text); //업체
	sArgument += "@SHPTO=" + quote(ed_SHPTO.Text); //SHPTO
	sArgument += "@PSPNO=" + quote(ed_PSPNO.Text); //품번

	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");

	if( ds_SJSZ100Q.GetRowCount() > 0){
		st_CARNM.Text = ds_SJSZ100Q.GetColumn(0,"CARNM");
		var tmpQy=0; 
		var tmpAm=0; 
		var setQy=0;
		var setAm=0;

		for(i=0; i<ds_SJSZ100Q.rowcount; i++){
			if(i!=0){
				tmpQy=ds_SJSZ100Q.GetColumn(i-1,"JBSQY");
				tmpAm=ds_SJSZ100Q.GetColumn(i-1,"JBSAM");		
				setQy=parseInt(tmpQy)-parseInt(ds_SJSZ100Q.GetColumn(i,"TQY"))+parseInt(ds_SJSZ100Q.GetColumn(i,"CQY"));
				setAm=parseInt(tmpAm)-parseInt(ds_SJSZ100Q.GetColumn(i,"TAM"))+parseInt(ds_SJSZ100Q.GetColumn(i,"CAM"));
				ds_SJSZ100Q.SetColumn(i,"JBSQY",setQy);
				ds_SJSZ100Q.SetColumn(i,"JBSAM",setAm);
			}
		}
	}	
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}
/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){
	var result = true;
	var pyvndVal = st_PYVND.Text;
	var pspnoVal = st_PSPNO.Text;
	ed_PYVND.BKColor="white";
	ed_PSPNO.BKColor="white";
	cmb_JSOYY.BKColor="white";


	if(cmb_JSOYY.Value==""){
		cmb_JSOYY.BKColor="paleturquoise";
		alert(setPopMessage("4059"));//[경고] 필수입력항목입니다. (적송년)"
		setMessage("4059");	
		result =false;
	}
	else if (pyvndVal == "" )
	{
		ed_PYVND.BKColor="paleturquoise";
		alert(setPopMessage("2516"));//[경고] 필수입력항목입니다. (업체번호)
		setMessage("2516");	
		result = false;
	}	
	else if (pspnoVal == "" )
	{
		ed_PSPNO.BKColor="paleturquoise";
		alert(setPopMessage("2501"));//[경고] 필수입력항목입니다. (품번)
		setMessage("2501");	
		result = false;
	}	
	return result;
}

