function MainGrid_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var plnym=""; //start년월
	var endym=parseInt(substr(cmb_JSOYY.Value,0,4))+1+"0331"; //end년월
	if(nRow==1){
		plnym=substr(cmb_JSOYY.Value,0,4)+"0401";
		getSJSZ100Q_SUB(plnym, endym, nRow);	
	}	
	if(nRow==2){
		plnym=substr(cmb_JSOYY.Value,0,4)+"0501";
		getSJSZ100Q_SUB(plnym, endym, nRow);	
	}
	if(nRow==3){
		plnym=substr(cmb_JSOYY.Value,0,4)+"0601";
		getSJSZ100Q_SUB(plnym, endym, nRow);	
	}
	if(nRow==4){
		plnym=substr(cmb_JSOYY.Value,0,4)+"0701";
		getSJSZ100Q_SUB(plnym, endym, nRow);	
	}
	if(nRow==5){
		plnym=substr(cmb_JSOYY.Value,0,4)+"0801";
		getSJSZ100Q_SUB(plnym, endym, nRow);	
	}
	if(nRow==6){
		plnym=substr(cmb_JSOYY.Value,0,4)+"0901";
		getSJSZ100Q_SUB(plnym, endym, nRow);	
	}
	if(nRow==7){
		plnym=substr(cmb_JSOYY.Value,0,4)+"1001";
		getSJSZ100Q_SUB(plnym, endym, nRow);	
	}
	if(nRow==8){
		plnym=substr(cmb_JSOYY.Value,0,4)+"1101";						
		getSJSZ100Q_SUB(plnym, endym, nRow);	
	}							
	if(nRow==9){
		plnym=substr(cmb_JSOYY.Value,0,4)+"1201";						
		getSJSZ100Q_SUB(plnym, endym, nRow);	
	}
	if(nRow==10){
		plnym=parseInt(substr(cmb_JSOYY.Value,0,4))+1+"0101";						
		getSJSZ100Q_SUB(plnym, endym, nRow);	
	}
	if(nRow==11){
		plnym=parseInt(substr(cmb_JSOYY.Value,0,4))+1+"0201";						
		getSJSZ100Q_SUB(plnym, endym, nRow);	
	}
	if(nRow==12){
		plnym=parseInt(substr(cmb_JSOYY.Value,0,4))+1+"0301";						
		getSJSZ100Q_SUB(plnym, endym, nRow);	
	}
}

function getSJSZ100Q_SUB(plnym,endym, nRow){

	var sKind		= "getSJSZ100Q_SUB";						
	var sMethodName = "service::pssallib/selectSJSZ100Q_SUB.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SJSZ100Q_SUB=ds_SJSZ100Q_SUB";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(ComboBoxPlant.Value); //사업부
	sArgument += " JSOYY=" + quote(substr(cmb_JSOYY.Value,0,4)); //적송년	
	sArgument += " PLNYM=" + quote(plnym); //선택시작년월
	sArgument += " ENDYM=" + quote(endym); //선택종료년월
	sArgument += " PYVND=" + quote(ed_PYVND.Text); //업체
	sArgument += " SHPTO=" + quote(ed_SHPTO.Text); //SHPTO
	sArgument += " PSPNO=" + quote(ed_PSPNO.Text); //품번
	http.Sync=true;	
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;	

	ds_SJSZ100Q_SUB.SetColumn(0,"JPSDT","");
	ds_SJSZ100Q_SUB.SetColumn(0,"TAXQY","");
	ds_SJSZ100Q_SUB.SetColumn(0,"TAXAM","");
	ds_SJSZ100Q_SUB.SetColumn(0,"JPSQY","");
	ds_SJSZ100Q_SUB.SetColumn(0,"JPSAM","전월이월");
	ds_SJSZ100Q_SUB.SetColumn(0,"JBSQY",ds_SJSZ100Q.GetColumn(nRow-1,"JBSQY"));
	ds_SJSZ100Q_SUB.SetColumn(0,"JBSAM",ds_SJSZ100Q.GetColumn(nRow-1,"JBSAM"));


	var tmpQy=0; 
	var tmpAm=0; 
	var setQy=0;
	var setAm=0;
	for(i=0; i<ds_SJSZ100Q_SUB.rowcount; i++){
		if(i!=0){
			tmpQy=ds_SJSZ100Q_SUB.GetColumn(i-1,"JBSQY");
			tmpAm=ds_SJSZ100Q_SUB.GetColumn(i-1,"JBSAM");		
			setQy=parseInt(tmpQy)-parseInt(ds_SJSZ100Q_SUB.GetColumn(i,"TAXQY"))+parseInt(ds_SJSZ100Q_SUB.GetColumn(i,"JPSQY"));
			setAm=parseInt(tmpAm)-parseInt(ds_SJSZ100Q_SUB.GetColumn(i,"TAXAM"))+parseInt(ds_SJSZ100Q_SUB.GetColumn(i,"JPSAM"));
			ds_SJSZ100Q_SUB.SetColumn(i,"JBSQY",setQy);
			ds_SJSZ100Q_SUB.SetColumn(i,"JBSAM",setAm);
		}
	}
	ds_SJSZ100Q_TRANS.AddRow();
	ds_SJSZ100Q_TRANS.SetColumn(0,"SUBMIT1",ed_PYVND.Text);
	ds_SJSZ100Q_TRANS.SetColumn(0,"SUBMIT2",st_PYVND.Text);
	ds_SJSZ100Q_TRANS.SetColumn(0,"SUBMIT3",ed_SHPTO.Text);
	ds_SJSZ100Q_TRANS.SetColumn(0,"SUBMIT4",ed_PSPNO.Text);
	ds_SJSZ100Q_TRANS.SetColumn(0,"SUBMIT5",st_PSPNO.Text);
	ds_SJSZ100Q_TRANS.SetColumn(0,"SUBMIT6",st_CARNM.Text);
	var str = dialog("pssallib::SJSZ100Q_SUB.xml","comps1="+ComboBoxPlant.Value+" "+"Plnym="+plnym+" ",300,300, "TitleBar=true");	
}

