function remoteCall(p_comps,p_vnd1,p_vnd2,B0,B1,B2,B3,B4,B5,B6,B7,B8,B9,B10,B11,B12){
	var pgmLib = "*LIBL";
	var pgmName = "SJSZ106RC";
	var param = "";
	var exeTime = 1200;
	//사업부별 공통ID(as400 USERID)로 실행
	var iDget = getRemotecallID(comps);
	if (iDget != "ERROR"){
		callUser = iDget;
		callPass = iDget;
	}else{
		alert(setPopMessage("1444"));
		setMessage("1444");	
		return false;
	}
	//리모트콜 실행
	param = p_comps + right("0000"+p_vnd1,4) + right("0000"+p_vnd2,4) +
			right("000000"+B0,6) +
			right("000000"+B1,6) + right("000000"+B2,6) + right("000000"+B3,6) + right("000000"+B4,6) + right("000000"+B5,6) + right("000000"+B6,6) +
			right("000000"+B7,6) + right("000000"+B8,6) + right("000000"+B9,6) + right("000000"+B10,6) + right("000000"+B11,6) + right("000000"+B12,6)
	;
	for(i=length(param)+1;i<=90;i++){
		param += " ";
	}
	param += callUser;		
	for(j=1;j<=10-length(callUser);j++){
		param += " ";
	}
	param += '000';

	WTA_RemoteCall0.RC_SetAttrValue(0,"2984",exeTime);	

	// 연결 (접속아이피 , 아이디, 비밀번호)	
	var rtn = WTA_RemoteCall0.RC_Connect(callIp,callUser,callPass);

		//접속성공시 
	if(rtn != 0){
		//프로그램 Call (라이브러리명, 프로그램명, 인자값, 크기);
		var rtn2 = WTA_RemoteCall0.RC_RmtPgmCall(pgmLib,pgmName,param,pgmSize);
		//정상적으로 수행했을 시
		if(rtn2 != 0){
			switch (substr(WTA_RemoteCall0.RC_GetReturnParm(1),100,3)){
			case "999" :  
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				return true;
			case "777" :  
				alert(WTA_RemoteCall0.RC_GetReturnParm(1));
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				alert(setPopMessage("1002")); //[경고] 처리 중 오류가 발생하였습니다.
				setMessage("1002");	
				return false;
			case "000" :  
				alert(WTA_RemoteCall0.RC_GetReturnParm(1));
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				alert(setPopMessage("1002")); //[경고] 처리 중 오류가 발생하였습니다.
				setMessage("1002");	
				return false;
			default :
				alert(WTA_RemoteCall0.RC_GetReturnParm(1));
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				alert(setPopMessage("1015")); //[경고] AS400 CL 처리 중 오류가 발생하였습니다.
				setMessage("1015");	
				return false;
			}
		}else{
			var bstrErrorMessage = WTA_RemoteCall0.bstrErrorMessage;
			WTA_RemoteCall0.RC_Disconnect();	//연결종료
			ReleaseCapture();
			SetWaitCursor(false);
			//오류 코드 경고창
			alert("에러 메세지=======>" + bstrErrorMessage);
			return false;
		}
	//접속실패시
	}else{
		var ulErrorCode = WTA_RemoteCall0.ulErrorCode;
		WTA_RemoteCall0.RC_Disconnect();	//연결종료
		ReleaseCapture();
		SetWaitCursor(false);
		//오류 코드 경고창
		alert("접속 실패 오류=======>" + ulErrorCode);
		return false;
	}
}
