 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = substr(ca_JSOYM.Value,0,6)+"년령별적송현황";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|년령별적송현황";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}


