function setExcel()
{
	var fName   = "세금계산서마스타";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|세금계산서마스타";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);

}

