var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");

var pgmLib = "*LIBL";
var pgmName = "SJTZ219RC";
var param = "";
var exeTime = 1200;

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("1"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	1.3
	getComCode(); //공장구분 선택
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	//ComboBoxPlant.Enable = false; 
}
function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}


function getSJTZ219Q(){

	if(length(trim(cmb_TAXYM.Value)) < 8){
		alert(setPopMessage("4036"));  //[[확인] 정산년월을 확인바랍니다.
		setMessage("4036");	
		cmb_TAXYM.SetFocus();
		return;
	}
	var p_pyvnd1,p_pyvnd2;
	p_pyvnd1 = iif (length(trim(ed_pyvnd1.Text)) == 0, "0000", trim(ed_pyvnd1.Text));
	p_pyvnd2 = iif (length(trim(ed_pyvnd2.Text)) == 0 ,"9999", trim(ed_pyvnd2.Text));
	//전월산출
	var byy,bmm;
	if(toNumber(substr(cmb_TAXYM.Value,4,2)) <= 4){
		byy=toNumber(substr(cmb_TAXYM.Value,0,4)) -1;
		bmm=toNumber(substr(cmb_TAXYM.Value,4,2))+12-4;
	}else{
		byy=toNumber(substr(cmb_TAXYM.Value,0,4));
		bmm=toNumber(substr(cmb_TAXYM.Value,4,2))-4;
	}
	var bym=byy+iif(bmm<10,'0'+bmm,''+bmm);
	//당월산출
	var ayy,amm;
	if(toNumber(substr(cmb_TAXYM.Value,4,2)) <= 3){
		ayy=toNumber(substr(cmb_TAXYM.Value,0,4)) -1;
		amm=toNumber(substr(cmb_TAXYM.Value,4,2))+12-3;
	}else{
		ayy=toNumber(substr(cmb_TAXYM.Value,0,4));
		amm=toNumber(substr(cmb_TAXYM.Value,4,2))-3;
	}
	var aym=ayy+iif(amm<10,'0'+amm,''+amm);


	//as400 USERID Get
	http.Sync = true;
	var sKind		= "getSsoAccount";						
	var sMethodName = "service::user/selectSsoAccount.do";   	// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             			// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_ssoaccount=ds_ssoaccount";    		// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
	sArgument += " USER_ID="    + quote(G_ds_user.GetColumn(0,"USER_ID"));
	sArgument += " INOUT_GUBN=" + quote("I");	
	sArgument += " COMPANY="    + quote(company);
	sArgument += " SYSTEM_SEQ=" + quote("4");
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_ssoaccount.GetRowCount() < 1){
		alert(setPopMessage("1014"));
		setMessage("1014");	
		return;
	}
	//사업부별 공통ID(as400 USERID)로 실행
	var iDget = getRemotecallID(comps);
	if (iDget != "ERROR"){
		callUser = iDget;
		callPass = iDget;
	}else{
		alert(setPopMessage("1444"));
		setMessage("1444");	
		return;
	}
	//리모트콜 실행(sjt219rc)
	param = ComboBoxPlant.Value + substr(cmb_TAXYM.Value,0,6) +bym +aym + "0000" + p_pyvnd1 + "0000" + p_pyvnd2;
	for(i=length(param)+1;i<=40;i++){
		param += " ";
	}
	param += callUser;		
	for(j=1;j<=10-length(callUser);j++){
		param += " ";
	}
	param += '000';

	SetWaitCursor(true);
	SetCapture();

	WTA_RemoteCall0.RC_SetAttrValue(0,"2984",exeTime);	

	// 연결 (접속아이피 , 아이디, 비밀번호)	
	var rtn = WTA_RemoteCall0.RC_Connect(callIp,callUser,callPass);

	//접속성공시 
	if(rtn != 0){
		//프로그램 Call (라이브러리명, 프로그램명, 인자값, 크기);
		var rtn2 = WTA_RemoteCall0.RC_RmtPgmCall(pgmLib,pgmName,param,pgmSize);
		//정상적으로 수행했을 시
		if(rtn2 != 0){
			ReleaseCapture();
			SetWaitCursor(false);
			if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "999") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				var sKind		= "getSJTZ219Q";						
				var sMethodName = "service::pssallib/selectSJTZ219Q.do";     		// 서버에서 호출 될 Method 명
				var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
				var sOutDataSet = "ds_SJTZ219Q=ds_SJTZ219Q";    				// 조회 후 돌려 받을 DataSet 명칭
				var sArgument   = "";
				sArgument += "@LIB1=" + quote(Lib1);
				sArgument += "@LIB2=" + quote(Lib2);
				sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
				//sArgument += "@PYVND1=" + quote(p_pyvnd1);
				//sArgument += "@PYVND2=" + quote(p_pyvnd2);
				sArgument += "@TAXYMD=" + quote(substr(cmb_TAXYM.Value,0,6)+"31");
				var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");
				if (str == 'T'){  //정상조회	
				}else if (str == 'B'){   //data값이 없을때
					alert(setPopMessage("1003"));
					setMessage("1003");
				}else if (str == 'E'){   //error발생시
					alert(setPopMessage("1002"));
					setMessage("1002");	
				}
				//alert(setPopMessage("1001")); //  정상적으로 처리가 완료되었습니다.	
				//setMessage("1001");	
			}else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "888") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				var ruser = trim(substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,10));
				var rcount = toNumber(substr(WTA_RemoteCall0.RC_GetReturnParm(1),60,2))-1;
				alert("사용자("+ruser+")외 "+rcount+"명이 사용중입니다. 작업이 불가능 합니다.");
				//alert(setPopMessage("1008"));	// 현재 시스템에 락이 걸렸습니다. 전산관리자에게 문의하세요.
				//setMessage("1008");
				return false;
			} else if (substr(WTA_RemoteCall0.RC_GetReturnParm(1),50,3) == "777") {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				alert(setPopMessage("1002")); //[경고] 처리 중 오류가 발생하였습니다.
				setMessage("1002");	
				return false;
			}else {
				WTA_RemoteCall0.RC_Disconnect();	//연결종료
				alert(setPopMessage("1015")); //[경고] AS400 CL 처리 중 오류가 발생하였습니다.
				setMessage("1015");	
				return false;
			}
		}else{
			var bstrErrorMessage = WTA_RemoteCall0.bstrErrorMessage;
			WTA_RemoteCall0.RC_Disconnect();	//연결종료
			ReleaseCapture();
			SetWaitCursor(false);
			//오류 코드 경고창
			alert("에러 메세지=======>" + bstrErrorMessage);
		}
	//접속실패시
	}else{
		var ulErrorCode = WTA_RemoteCall0.ulErrorCode;
		WTA_RemoteCall0.RC_Disconnect();	//연결종료
		ReleaseCapture();
		SetWaitCursor(false);
		//오류 코드 경고창
		alert("접속 실패 오류=======>" + ulErrorCode);
	}
}

