function getDesign(){
	MainGrid_etc.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid_etc.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid_etc.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid_etc.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid_etc.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid_etc.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	//MainGrid_etc.Width = G_ds_design.GetColumn(0,"Width");
	MainGrid_etc.Top = G_ds_design.GetColumn(0,"Top");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");		
}

function getComCode(){

	http.Sync 	= true;

	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	//ds_VComCode_PLNT.Copy("ds_ComCode_PLNT");

	//cb_searchCodeName1.index = 0;
	cb_searchCodeName1.Value = comps;
}


/********************************************************************** 
 * 내용설명 : 화면조회
 * 주요변수 : 
 **********************************************************************/ 
function getSLTZ010(){
	if(length(trim(st_pyvnd.Text)) < 1){
		alert(setPopMessage("1502"));  //[확인] 거래처코드 확인바랍니다.
		setMessage("1502");	
		ed_pyvnd.SetFocus();
		return;
	}	

	//조회
	var sKind		= "getSLTZ010";						
	var sMethodName = "service::pssallib/selectSLTZ010.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SLTZ010=ds_SLTZ010";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";

	sArgument += "@LIB1=" + quote(Lib1);
	sArgument += "@LIB2=" + quote(Lib2);
	sArgument += "@COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += "@PYVND=" + quote(ed_pyvnd.Text);
	sArgument += "@INOUT=" + quote(ca_INOUT.Value);
	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");	
	//Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");	
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}

}

/********************************************************************** 
 * 내용설명 : 저장
 * 주요변수 : 
 **********************************************************************/ 
 function setSLTZ010(){

	if(length(trim(st_pyvnd.Text)) < 1){
		alert(setPopMessage("1502"));  //[확인] 거래처코드 확인바랍니다.
		setMessage("1502");	
		ed_pyvnd.SetFocus();
		return;
	}	

 	http.Sync = true;
	var sKind		= "SAVE_SLTZ010";						
	var sMethodName = "service::pssallib/insertSLTZ010.do";
	var sInDataSet  = "ds_SLTZ010=ds_SLTZ010:U";
	var sOutDataSet = "ds_SLTZ010_CHECK=ds_SLTZ010_CHECK";
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);

	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
 }

/********************************************************************** 
 * 내용설명 : 엑셀파일로 저장
 * 주요변수 : 
 **********************************************************************/ 
function setExcel()
{
	var fName   = "용차사용박스단가등록";
	var arrSheets  =  Array();  

	arrSheets[0]  = "MainGrid|용차사용박스단가등록";
	createExcelSheetS2(FileDialog0, fName ,arrSheets);
}


/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_SLTZ010_CHECK.GetRowCount() > 0){			
		for(var i=0;i<ds_SLTZ010_CHECK.GetRowCount();i++){		
			alert(ds_SLTZ010_CHECK.GetColumn(i,"MKLOC") + "=" + ds_SLTZ010_CHECK.GetColumn(i,"RTMSG"));	
		}		
		getSLTZ010();        //error항목이 존재시에는 재조회		
		return;		
	}	
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else 
	{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

