function fn_Input()
{
	if(length(trim(st_pyvnd.Text)) < 1){
		alert(setPopMessage("1502"));  //[확인] 거래처코드 확인바랍니다.
		setMessage("1502");	
		ed_pyvnd.SetFocus();
		return;
	}
	if(checkInsertCount("ds_SLTZ010")== false) return;
	if(ds_SLTZ010.rowcount < 1) fn_Search();
	ds_SLTZ010.FireEvent=false;  //데이터셋의 이벤트가 발생하지 않도록 셋팅
	ds_SLTZ010.AddRow();
	ds_SLTZ010.SetColumn(ds_SLTZ010.Row, "COMPS", cb_searchCodeName1.Value); // default값 설정
	ds_SLTZ010.SetColumn(ds_SLTZ010.Row, "PYVND", ed_pyvnd.Text); // default값 설정
	ds_SLTZ010.SetColumn(ds_SLTZ010.Row, "SHPTO", "0"); // default값 설정
	ds_SLTZ010.SetColumn(ds_SLTZ010.Row, "INOUT", ca_INOUT.Value); // default값 설정
	ds_SLTZ010.FireEvent=true;
}

