function imgMaker_OnClick(obj,nX,nY)
{
	var sTemp=trim(ed_pyvnd.Text);
	if (length(trim(ed_pyvnd.Text)) == 0) sTemp="zzzzzzzzzz";
	var selectnumber = "B0";
	var rtn = dialog("Common::commonPopup.xml","SEL=" + selectnumber + " " + "CODE=" + sTemp,584,528);
	if (length(rtn) != 0){
		ed_pyvnd.Text = rtn[0];
		st_pyvnd.Text = rtn[1];
	}
}

function ed_pyvnd_OnCharChanged(obj,strPreText,strPostText)
{
	st_pyvnd.Text = "";
}

function ed_pyvnd_OnKillFocus(obj)
{
	if(length(trim(obj.Text)) < 1) return;
	//득의선Check
	http.usewaitcursor = false;
	http.Sync 	= true;
	var sKind		= "getOGMPrtno";		
	var sMethodName = "service::pscomlib/selectCUSMAS1.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_CUSMAS1=ds_CUSMAS1";
	var sArgument   = "";
		sArgument  += " LIB1=" + quote(Lib1);
		sArgument  += " CUSNO=" + quote(ed_pyvnd.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	if (ds_CUSMAS1.GetRowCount() < 1){
		alert(setPopMessage("1202")); //[확인] 거래처코드 확인바랍니다.
		setMessage("1202");	
		ed_pyvnd.SetFocus();
		return;
	}
	st_pyvnd.Text=ds_CUSMAS1.GetColumn(0,"CUSNM");
	http.usewaitcursor = true;
}


function ds_SLTZ010_CanColumnChange(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if (strColumnID == "MKLOC"){
		//중복Check
		if(ds_SLTZ010.FindRow("MKLOC", trim(varNewValue)) >= 0){
			alert(setPopMessage("1520"));  //[경고] 이미 등록된 자료입니다.
			setMessage("1520");	
			return false;
		}
		//납품장소check
		http.Sync = true;
		var sKind		= "getmst050pf";		
		var sMethodName = "service::pscomlib/selectMST050PF.do"; 
		var sInDataSet  = "";
		var sOutDataSet = "ds_MST050PF=ds_MST050PF";
		var sArgument   = "";
			sArgument  += " LIB2=" + quote(Lib2);
			sArgument  += " COMPS=" + quote(cb_searchCodeName1.Value);
			sArgument  += " GRPCD=" + quote("LOC");
			sArgument  += " MSTCD=" + quote(varNewValue);
		Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
		http.Sync = false;
		if (ds_MST050PF.GetRowCount() < 1){
			alert(setPopMessage("4008")); //[확인] 납품장소를 확인바랍니다.
			setMessage("4008");	
			return false;
		}
		MKLOC_NM=ds_MST050PF.GetColumn(0,"MSTRM");
	}
	http.usewaitcursor = true;
}

function ds_SLTZ010_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	http.usewaitcursor = false;
	if (strColumnID == "MKLOC"){
		ds_SLTZ010.SetColumn(nRow, "MSTRM", MKLOC_NM); // 거래처명 설정
	}
	http.usewaitcursor = true;
}
