/********************************************************************** 
 * 내용설명 : 용차사용신청 생성
 * 주요변수 : 
 **********************************************************************/ 
 function setSLTZ100UC(){
	if (ds_SEND.GetRowCount() < 1) {
		alert("[경고] 생성할 data가 없습니다. 확인바랍니다."); 
		return;
	}
	if(length(trim(Div1.st_mstrm.Text)) < 1){
		alert(setPopMessage("4019")); 
		setMessage("4019");	
		Div1.ed_CFMCD.SetFocus();
		return;
	}
	for(var i=0;i<ds_SEND.GetRowCount();i++){
		if (ds_SEND.getcolumn(i,"UPERR") != "0"){
			alert("[경고] error항목 존재. 생성가능여부를 확인하시고 다시 작업바랍니다."); 
			return;
		}
	}
	if(confirm("생성하시겠습니까? ") == false) return;

 	http.Sync = true;
	var sKind		= "SAVE_SLTZ100UC";						
	var sMethodName = "service::pssallib/insertSLTZ100UC.do";
	var sInDataSet  = "ds_SEND=ds_SEND";
	var sOutDataSet = "ds_SLTZ100UC_CHECK=ds_SLTZ100UC_CHECK";
	var sArgument   = "";
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " COMPS=" + quote(Div1.cb_searchCodeName1.Value);
	sArgument += " INOUT=" + quote(Div1.ca_INOUT.Value);
	sArgument += " EXPCD=" + quote(Div1.ca_EXPCD.Value);
	sArgument += " CFMCD=" + quote(Div1.ed_CFMCD.Text);
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
	http.Sync = false;
 }

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (svcId = "SAVE_SLTZ100UC"){
		if (ds_SLTZ100UC_CHECK.GetRowCount() > 0){			
			for(var i=0;i<ds_SLTZ100UC_CHECK.GetRowCount();i++){		
				alert(ds_SLTZ100UC_CHECK.GetColumn(i,"LNTNO") + " ="
					+ ds_SLTZ100UC_CHECK.GetColumn(i,"RTMSG"));	
			}
			return;		
		}
	}

	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else 
	{
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

function chkExcelData(){
	//ds_SLTZ100UC_BOX.ClearData();
	for(var z=0;z<ds_SEND.GetRowCount();z++){
		ds_SEND.SetColumn(z,"UPERR","0");
		//if (!((trim(ds_SEND.GetColumn(z,"EXPCD"))) == 'Y' || (trim(ds_SEND.GetColumn(z,"EXPCD"))) == 'N')) ds_SEND.SetColumn(z,"UPERR","4"); //차량처리(필수입력)
		if (length(trim(ds_SEND.GetColumn(z,"USEDT"))) != 8) ds_SEND.SetColumn(z,"UPERR","1"); //사용일자(필수입력)
		if (!numberChk(ds_SEND.GetColumn(z,"USEDT"))) ds_SEND.SetColumn(z,"UPERR","1");
		if (!isDate(ds_SEND.GetColumn(z,"USEDT"))) ds_SEND.SetColumn(z,"UPERR","1");
		if (length(ds_SEND.GetColumn(z,"MKLOC")) > 2) ds_SEND.SetColumn(z,"UPERR","2");
		if (ds_MST050_MKLOC.FindRow("MSTCD",ds_SEND.GetColumn(z,"MKLOC")) == -1) ds_SEND.SetColumn(z,"UPERR","2");  //납품장소 오류
		if (length(trim(ds_SEND.GetColumn(z,"PYVND"))) != 4) ds_SEND.SetColumn(z,"UPERR","3");
		if (!numberChk(ds_SEND.GetColumn(z,"PYVND"))) ds_SEND.SetColumn(z,"UPERR","3");
		if (ds_Select.FindRow("A1",ds_SEND.GetColumn(z,"PYVND")) == -1) ds_SEND.SetColumn(z,"UPERR","3");  //납입처 오류
		if (length(ds_SEND.GetColumn(z,"CARSQ")) > 2) ds_SEND.SetColumn(z,"UPERR","4");
		if (ds_MST050_CARSQ.FindRow("MSTCD",ds_SEND.GetColumn(z,"CARSQ")) == -1) ds_SEND.SetColumn(z,"UPERR","4");  //출발장소 오류
		if (length(trim(ds_SEND.GetColumn(z,"CARTN"))) > 4) ds_SEND.SetColumn(z,"UPERR","5");
		if (!numberChk(ds_SEND.GetColumn(z,"CARTN"))) ds_SEND.SetColumn(z,"UPERR","5");
		if (length(trim(ds_SEND.GetColumn(z,"USEGB"))) != 1) ds_SEND.SetColumn(z,"UPERR","6");
		if (ds_MST050_USEGB.FindRow("MSTCD",ds_SEND.GetColumn(z,"USEGB")) == -1) ds_SEND.SetColumn(z,"UPERR","6");  //운행구분 오류
		if (length(trim(ds_SEND.GetColumn(z,"TRNCD"))) > 1) ds_SEND.SetColumn(z,"UPERR","7"); //경유지역 오류
		if (length(trim(ds_SEND.GetColumn(z,"TRNCD"))) == 1){
			if (ds_MST050_TRNCD.FindRow("MSTCD",ds_SEND.GetColumn(z,"TRNCD")) == -1) ds_SEND.SetColumn(z,"UPERR","7");
		}
		if (length(trim(ds_SEND.GetColumn(z,"ARVDT"))) < 1 || length(trim(ds_SEND.GetColumn(z,"ARVDT"))) > 8) ds_SEND.SetColumn(z,"UPERR","8");
		if (length(trim(ds_SEND.GetColumn(z,"ARVDT"))) > 0){
			if (!numberChk(ds_SEND.GetColumn(z,"ARVDT"))) ds_SEND.SetColumn(z,"UPERR","8");
			if (!isDate(ds_SEND.GetColumn(z,"ARVDT"))) ds_SEND.SetColumn(z,"UPERR","8");
		}
		if (length(trim(ds_SEND.GetColumn(z,"ARVTM"))) < 1 || length(trim(ds_SEND.GetColumn(z,"ARVTM"))) > 4) ds_SEND.SetColumn(z,"UPERR","9");
		if (length(trim(ds_SEND.GetColumn(z,"ARVTM"))) > 0){
			if (!numberChk(ds_SEND.GetColumn(z,"ARVTM"))) ds_SEND.SetColumn(z,"UPERR","9");
			if(!Time_Check(ds_SEND.GetColumn(z,"ARVTM"))) ds_SEND.SetColumn(z,"UPERR","9");
		}
		if (length(trim(ds_SEND.GetColumn(z,"CARNO"))) > 6) ds_SEND.SetColumn(z,"UPERR","10");
		if (length(trim(ds_SEND.GetColumn(z,"USEAM"))) > 7) ds_SEND.SetColumn(z,"UPERR","11");
		if (length(trim(ds_SEND.GetColumn(z,"USEAM"))) > 0){
			if (!numberChk(ds_SEND.GetColumn(z,"USEAM"))) ds_SEND.SetColumn(z,"UPERR","11");
		}
		if (length(trim(ds_SEND.GetColumn(z,"DRVPN"))) > 8) ds_SEND.SetColumn(z,"UPERR","12");
		if (length(trim(ds_SEND.GetColumn(z,"DRVPN"))) > 0){
			if (!numberChk(ds_SEND.GetColumn(z,"DRVPN"))) ds_SEND.SetColumn(z,"UPERR","12");
			if (ds_MST600_DRVPN.FindRow("VNDOR",trim(ds_SEND.GetColumn(z,"DRVPN"))) == -1) ds_SEND.SetColumn(z,"UPERR","12");
		}
		if (length(trim(ds_SEND.GetColumn(z,"JPSNO1"))) > 7 || length(trim(ds_SEND.GetColumn(z,"JPSNO1"))) < 1) ds_SEND.SetColumn(z,"UPERR","13");
		if (length(trim(ds_SEND.GetColumn(z,"JPSNO2"))) > 7) ds_SEND.SetColumn(z,"UPERR","13");
		if (length(trim(ds_SEND.GetColumn(z,"JPSNO3"))) > 7) ds_SEND.SetColumn(z,"UPERR","13");
		if (length(trim(ds_SEND.GetColumn(z,"JPSNO4"))) > 7) ds_SEND.SetColumn(z,"UPERR","13");
		if (length(trim(ds_SEND.GetColumn(z,"JPSNO5"))) > 7) ds_SEND.SetColumn(z,"UPERR","13");
		if (length(trim(ds_SEND.GetColumn(z,"JPSNO6"))) > 7) ds_SEND.SetColumn(z,"UPERR","13");

		if (length(trim(ds_SEND.GetColumn(z,"ARVTM"))) < 1) {
			ds_SEND.SetColumn(Z,"ARVTM","0");
		}
		if (trim(ds_SEND.GetColumn(z,"EXPCD")) == "N") {
			ds_SEND.SetColumn(Z,"USEAM","0");
		}
		if (length(trim(ds_SEND.GetColumn(z,"USEAM"))) < 1) {
			ds_SEND.SetColumn(Z,"USEAM","0");
		}
		if (trim(ds_SEND.GetColumn(z,"EXPCD")) == "Y" && ds_SEND.GetColumn(z,"USEAM")=="0") {
			ds_SEND.SetColumn(z,"UPERR","11");
		}
		if (length(trim(ds_SEND.GetColumn(z,"DRVPN"))) < 1) {
			if (comps == "E1"){
				ds_SEND.SetColumn(Z,"DRVPN","8810");
			}else{
				ds_SEND.SetColumn(Z,"DRVPN","0");
			}
		}
	}
	return "Y";
}
/********************************************************************** 
 * 내용설명 : 시간형식Check
 **********************************************************************/ 
function Time_Check(strText)
{
	var strChk_t = replace(strText,':','');
	var strChk = right("0000"+strChk_t,4);
	if((left(strChk,2) > 24) || (right(strChk,2) > 59)){
		return false;
	}
	return true;
}

