/********************************************************************** 
 * 내용설명 : 공통코드 조회
 * 주요변수 : 
 **********************************************************************/ 
function getDesign(){
	ubi0.Width = G_ds_design.GetColumn(0,"Width");
	ubi0.Top = G_ds_design.GetColumn(0,"Top");
	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

function getComCode(){
	http.Sync 	= true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;
	ds_VComCode_PLNT.Copy("ds_ComCode_PLNT");

	cb_searchCodeName1.Value = comps;
}

/********************************************************************** 
 * 내용설명 : 발행
 * 주요변수 : 
 **********************************************************************/ 
 function prtSMBZ101R()
 {
	if(length(trim(ed_SIPNO_F.Value)) < 1){
		alert(setPopMessage("4035"));  //
		setMessage("4035");	
		ed_SIPNO_F.SetFocus();
		return;
	}

	/* 2018.04.19 삭제
	var JDELib4;
	if (Lib2 == "PSEDBLIB"){
		JDELib4 = "CLTDTAE";
	}else{
		JDELib4 = "CLTDTAS";
	}
	*/

	var p_sipno_t;
	p_sipno_t = iif (length(trim(ed_SIPNO_T.Value)) == 0 , '99999999', trim(ed_SIPNO_T.Value));

	http.Sync 	= true;
	var sKind		= "getSMBZ101R";						
	var sMethodName = "service::pssallib/selectSMBZ101R.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_SMBZ101R=ds_SMBZ101R";
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " LIB1=" + quote(Lib1);
	//sArgument += " LIB4=" + quote(JDELib4);
	sArgument += " LIB4=" + quote(Lib4);
	sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += " SIPNO_F=" + quote(trim(ed_SIPNO_F.Text)); //
	sArgument += " SIPNO_T=" + quote(trim(ed_SIPNO_T.Text)); //
	//sArgument += " GROUP=" + quote(ComboBoxClass.Value);      
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync	= false;

	ubi0.setJrfFileDir(G_baseURL+"/ui/PSSALLIB/REPORT/");
	ubi0.setJrfFileName("SMBZ101R.jrf");
	ubi0.SetDataset("ds_SMBZ101R", "ds_SMBZ101R");	
//	ubi0.SetArgument("DHCODE", search_dhcode.Text+"("+search_dhcode.Value+")");
//	ubi0.SetArgument("WRKDT", parent.search_wrkdt.Text);	
//	ubi0.SetArgument("CURRENT_TIME", substr(Getdate(),8,2)+":"+substr(Getdate(),10,2)+":"+substr(Getdate(),12,2));
	ubi0.Retrieve();
}



