}


/********************************************************************** 
 * 내용설명 : 공통코드 조회
 * 주요변수 : 
 **********************************************************************/ 
function getDesign(){
	ubi0.Width = G_ds_design.GetColumn(0,"Width");
	ubi0.Top = G_ds_design.GetColumn(0,"Top");
	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Height = G_ds_design.GetColumn(0,"SBHeight");	
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

function getComCode(){
	http.Sync 	= true;
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync 	= false;
	cb_searchCodeName1.Value = comps;
}

/********************************************************************** 
 * 내용설명 : 발행
 * 주요변수 : 
 **********************************************************************/ 
 function prtSMBZ122R()
 {
	if(length(cal_JSOYM.Value)<1){
		alert(setPopMessage("4032"));  
		setMessage("4032");	
		return;
	}
	var jsoyy;
	if(toNumber(substr(cal_JSOYM.Value,4,2)) < 4){
		jsoyy = toNumber(substr(cal_JSOYM.Value,0,4)) - 1;
	}else{
		jsoyy = toNumber(substr(cal_JSOYM.Value,0,4));
	}
	var b_misxx,debxx,crexx,misxx;
	if(toNumber(substr(cal_JSOYM.Value,4,2)) == 4){
		b_misxx = "A.JBSAM AS BMISAMT";
	}else{
		b_misxx = "A.MIS"+substr(AddMonth(cal_JSOYM.Value,-1),4,2)+" AS BMISAMT";
	}
	debxx="A.DEB"+substr(cal_JSOYM.Value,4,2)+" AS DEBAMT";
	crexx="A.CRE"+substr(cal_JSOYM.Value,4,2)+" AS CREAMT";
	misxx="A.MIS"+substr(cal_JSOYM.Value,4,2)+" AS MISAMT";

	http.Sync 	= true;
	var sKind		= "getSMBZ122R";						
	var sMethodName = "service::pssallib/selectSMBZ122R.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_SMBZ122R=ds_SMBZ122R ds_SMBZ122R_TOT=ds_SMBZ122R_TOT";
	var sArgument   = "";
	sArgument += " LIB2=" + quote(Lib2);
	sArgument += " LIB1=" + quote(Lib1);
	sArgument += " COMPS=" + quote(cb_searchCodeName1.Value);
	sArgument += " YYMM=" + quote(substr(cal_JSOYM.Value,0,6)); //선택년월
	sArgument += " MM=" + quote(substr(cal_JSOYM.Value,4,2)); //선택월
	sArgument += " YYYY=" + quote(jsoyy); //계산년
	sArgument += " BMISMM=" + quote(b_misxx); 
	sArgument += " DEBMM=" + quote(debxx);
	sArgument += " CREMM=" + quote(crexx);
	sArgument += " MISMM=" + quote(misxx); 
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync	= false;

	ubi0.setJrfFileDir(G_baseURL+"/ui/PSSALLIB/REPORT/");
	//C:\eclipse\workspace\DENSO\WebContent
	//ubi0.setJrfFileDir("C:/eclipse/workspace/DENSO/WebContent"+"/ui/PSSALLIB/REPORT/");
	ubi0.setJrfFileName("SMBZ122R.jrf");
	ubi0.SetDataset("ds_SMBZ122R", "ds_SMBZ122R");	
	ubi0.SetDataset("ds_SMBZ122R_TOT", "ds_SMBZ122R_TOT");	
	ubi0.SetArgument("COMPS", cb_searchCodeName1.Value);
	ubi0.SetArgument("YM", substr(cal_JSOYM.Value,0,6));
//	ubi0.SetArgument("WRKDT", parent.search_wrkdt.Text);	
//	ubi0.SetArgument("CURRENT_TIME", substr(Getdate(),8,2)+":"+substr(Getdate(),10,2)+":"+substr(Getdate(),12,2));
	ubi0.Retrieve();
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");
	}
}

