var strSeparator = "	";
var Lib1 = G_ds_user.GetColumn(0,"LIB1"); // cigma library(pereslib,psreslib)
var Lib2 = G_ds_user.GetColumn(0,"LIB2"); // 회사 library(psedblib,pscdblib)
var Lib3 = G_ds_user.GetColumn(0,"LIB3"); // 사업부 library(psedblib,pscdblib,pshdblib,pssdblib)
var company = G_ds_user.GetColumn(0,"COMPANY");
var comps = G_ds_user.GetColumn(0,"PLANT_CD");
var userId = G_ds_user.GetColumn(0,"USER_ID");

function on_LoadCompleted(obj){
	getTitle();	
	getDesign();
	gFcOnTopButtonColor("2.2"); //상단 버튼 활성화 여부 (기본:0,조회:1,변경:2)	1.3
	getComCode(); //공장구분 선택
	ed_PYVND.Text=SPYVND;
	ed_CARPS.Text=SCARPS;
	ed_SHPTO.Text=SSHPTO;
	ed_PYVND.SetFocus();
	ed_CARPS.SetFocus();
	btnQuery.SetFocus();
}

function getComCode(){
	var sKind		= "getComCode";						
	var sMethodName = "service::login/selectComCode.do"; 
	var sInDataSet  = "";
	var sOutDataSet = "ds_ComCode_PLNT=ds_ComCode_PLNT";
	var sArgument   = "";
		sArgument   += " CLASS='PLNT'";
		sArgument   += " CODE1=" + quote(company);		
	http.Sync=true;
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "");
	http.Sync=false;
	ComboBoxPlant.Value=comps;
	//ComboBoxPlant.Enable = false; 
}

function getDesign(){
	//MainGrid.ToolTipText = setPopMessage("2053");
	MainGrid.BkColor = G_ds_design.GetColumn(0,"BkColor");
	MainGrid.BkColor2 = G_ds_design.GetColumn(0,"BkColor2");
	MainGrid.BkSelColor = G_ds_design.GetColumn(0,"BkSelColor");
	MainGrid.Face3dColor = G_ds_design.GetColumn(0,"Face3dColor");
	MainGrid.BoldHead = G_ds_design.GetColumn(0,"BoldHead");
	MainGrid.HeadHeight = G_ds_design.GetColumn(0,"HeadHeight");
	MainGrid.Width = G_ds_design.GetColumn(0,"Width");

	searchBar.Width = G_ds_design.GetColumn(0,"Width");
	searchBar.Top = G_ds_design.GetColumn(0,"SBTop");	
}

function getSomc010(){

	if(!validationChk()){
		return;
	}
	var sKind		= "getSomc010";						
	var sMethodName = "service::pssallib/selectSOMC010.do";     		// 서버에서 호출 될 Method 명
	var sInDataSet  = "";                             				// 자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SOMC010=ds_SOMC010";    				// 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += "@LIB1=" + quote(Lib1);
		sArgument += "@LIB2=" + quote(Lib2);
		sArgument += "@COMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += "@PYVND=" + quote(ed_PYVND.Text); //득의선
		sArgument += "@SHPTO=" + quote(ed_SHPTO.Text); //득의선
		sArgument += "@CARPS=" + quote(ed_CARPS.Text); //SHIPTO
	var str = dialog("Common::PGMLOAD_SAL.xml","sKind=" + sKind + " " + "sMethodName=" + sMethodName + " " + "sOutDataSet=" + sOutDataSet + " " + "sArgument=" + sArgument,272,64,"TitleBar=false");

	if (str == 'T'){  //정상조회
		alert(setPopMessage("1004"));
		setMessage("1004");
	}else if (str == 'B'){   //data값이 없을때
		alert(setPopMessage("1003"));
		setMessage("1003");	
	}else if (str == 'E'){   //error발생시
		alert(setPopMessage("1002"));
		setMessage("1002");	
	}
}

/********************************************************************** 
 * 내용설명 : validation 체크
 **********************************************************************/ 

function validationChk(){

	var result = true;
	var spyvnd = st_PYVND.Text;
	var scarps = st_CARPS.Text;
		ed_PYVND.BKColor="white";
		ed_CARPS.BKColor="white";	

	if (spyvnd == "")
	{
		ed_PYVND.BKColor="paleturquoise";
		alert(setPopMessage("1502"));  //[확인] 거래처코드 확인바랍니다.
		setMessage("1502");	
		result = false;
	}
	else if (scarps == "")
	{
		ed_CARPS.BKColor="paleturquoise";
		alert(setPopMessage("4675"));//[알림]차종을 입력하세요.
		setMessage("4675");	
		result = false;
	}	
	return result;
}

 //등록시 업체코드 존재유무 체크함. 없으면 동록불가.
function setSomc010(){

	if(ds_SOMC010.GetRowType(ds_SOMC010.currow)=="update" ||ds_SOMC010.GetRowType(ds_SOMC010.currow)=="insert" ){
		if(!ds_SOMC010_CanRowPosChange()){
			return;
		}
	} 

	if(length(trim(st_PYVND.Text)) < 1){
		alert(setPopMessage("1502"));  //[확인] 거래처코드 확인바랍니다.
		setMessage("1502");	
		ed_PYVND.SetFocus();
		return;
	}
	if(length(trim(st_CARPS.Text)) < 1){
		alert(setPopMessage("4675"));  //[확인] 거래처코드 확인바랍니다.
		setMessage("4675");	
		ed_CARPS.SetFocus();
		return;
	}		

	var sKind		= "setSomc010";						
	var sMethodName = "service::pssallib/insertSOMC010.do";     // 서버에서 호출 될 Method 명
	var sInDataSet  = "ds_SOMC010=ds_SOMC010:U";               //자료 저장 시 보낼 DataSet 명칭
	var sOutDataSet = "ds_SOMC010_CHECK=ds_SOMC010_CHECK";  							     // 조회 후 돌려 받을 DataSet 명칭
	var sArgument   = "";
		sArgument += " LIB1=" + quote(Lib1);
		sArgument += " LIB2=" + quote(Lib2);
		sArgument += " SCOMPS=" + quote(ComboBoxPlant.Value); //사업부
		sArgument += " SPYVND=" + quote(ed_PYVND.Text); //업체
		sArgument += " SSHPTO=" + quote(ed_SHPTO.Text); //업체
		sArgument += " SCARPS=" + quote(ed_CARPS.Text); //업체
		sArgument += " SUSER=" + quote(userId); //사용자
	Transaction(sKind, sMethodName, sInDataSet, sOutDataSet, sArgument, "callback");
}

/********************************************************************** 
 * 내용설명 : Transaction 리턴후 실행 함수
 * 주요변수 : 
 * 			- sMethodName	Transaction 고유 ID
 * 			- ErrorCode 	에러코드
 * 			- ErrorMSG 		에러 메세지
 **********************************************************************/ 
function callback(svcId, rtnCd, rtnMsg){
	if (ds_SOMC010_CHECK.GetRowCount() > 0){
		for(var i=0;i<ds_SOMC010_CHECK.GetRowCount();i++){
			alert("유형 : " +ds_SOMC010_CHECK.GetColumn(i,"IORDT") +  ", 품번 : " +ds_SOMC010_CHECK.GetColumn(i,"PSPNO") + " = " + ds_SOMC010_CHECK.GetColumn(i,"RTMSG"));
		}
		getSomc010();  //error항목이 존재시에는 재조회
		return;
	}
	// tran 실패
	if(rtnCd<0){
		alert(setPopMessage("1002"));
		setMessage("1002");

	}else 
	{
		//getSalz101();  //error항목이 존재시에는 재조회
		alert(setPopMessage("1001"));
		setMessage("1001");	
	}
}

