function fn_Input()
{
	if(!validationChk()){
		return;
	}
	if(checkInsertCount("ds_SOMC010")== false) return;
	if(ds_SOMC010.rowcount < 1) fn_Search();  
	ds_SOMC010.AddRow();
	ds_SOMC010.SetColumn(ds_SOMC010.currow,"COMPS",ComboBoxPlant.Value);
	ds_SOMC010.SetColumn(ds_SOMC010.currow,"PYVND",ed_PYVND.Text);
	ds_SOMC010.SetColumn(ds_SOMC010.currow,"CARPS",ed_CARPS.Text);
	if(length(ed_SHPTO.Text)==0){
		ds_SOMC010.SetColumn(ds_SOMC010.currow,"SHPTO",0);
	}
	else{
		ds_SOMC010.SetColumn(ds_SOMC010.currow,"SHPTO",ed_SHPTO.Text);	
	}
}

function fn_Delete()
{
	if(ds_SOMC010.rowcount < 1) return;			//삭제가 없으면 건너뛰기		
	ds_SOMC010.DeleteRow(ds_SOMC010.currow);	//삭제		
	alert(setPopMessage("1011"));				//[알림] 화면에서만 제거되었습니다. 저장하시면 삭제됩니다.				
}

